// App.js
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Register from "./pages/Register";
import Login from "./pages/Login";
import AdminDashboard from "./pages/AdminDashboard";
import StudentDashboard from "./pages/StudentDashboard";
import TerminalPage from "./pages/TerminalPage"; // Add this import
import ProtectedRoute from "./components/ProtectedRoute"; // import your ProtectedRoute

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Navigate to="/login" />} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />

        {/* Admin Dashboard - only accessible by admin */}
        <Route
          path="/admin"
          element={
            <ProtectedRoute allowedRole="admin">
              <AdminDashboard />
            </ProtectedRoute>
          }
        />

        {/* Student Dashboard - only accessible by student */}
        <Route
          path="/student"
          element={
            <ProtectedRoute allowedRole="student">
              <StudentDashboard />
            </ProtectedRoute>
          }
        />

        {/* Terminal Page - accessible by students (you can add protection if needed) */}
        <Route 
          path="/terminal" 
          element={<TerminalPage />} 
        />

        {/* Alternative: If you want to protect the terminal page too */}
        {/* 
        <Route
          path="/terminal"
          element={
            <ProtectedRoute allowedRole="student">
              <TerminalPage />
            </ProtectedRoute>
          }
        />
        */}
      </Routes>
    </Router>
  );
}

export default App;
