import React, { useState, useEffect } from "react";

const AdminDashboard = () => {
  const [user, setUser] = useState(null);
  const [servers, setServers] = useState([]);
  const [selectedServer, setSelectedServer] = useState(null);
  const [serverDetails, setServerDetails] = useState(null);
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('sessions'); // sessions, commands, users
  const [realtimeActivity, setRealtimeActivity] = useState([]);

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
      loadServers();
    }
  }, []);

  const loadServers = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/admin/servers', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setServers(data.servers);
      }
    } catch (error) {
      console.error('Error loading servers:', error);
    }
  };

  const loadServerDetails = async (serverIp) => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/admin/servers/${serverIp}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setServerDetails(data);
        setSelectedServer(serverIp);
      }
    } catch (error) {
      console.error('Error loading server details:', error);
    }
    setLoading(false);
  };

  const loadRealtimeActivity = async (serverIp) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/admin/servers/${serverIp}/realtime`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setRealtimeActivity(data.recent_activity);
      }
    } catch (error) {
      console.error('Error loading realtime activity:', error);
    }
  };

  const formatDuration = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    
    if (hours > 0) {
      return `${hours}h ${minutes}m ${secs}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${secs}s`;
    } else {
      return `${secs}s`;
    }
  };

  const formatTime = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  const handleServerClick = (serverIp) => {
    loadServerDetails(serverIp);
    loadRealtimeActivity(serverIp);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const refreshData = () => {
    loadServers();
    if (selectedServer) {
      loadServerDetails(selectedServer);
      loadRealtimeActivity(selectedServer);
    }
  };

  if (!user) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <div className="bg-white shadow-md">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold text-gray-800">Admin Dashboard</h1>
              <p className="text-gray-600">Welcome, {user.name}</p>
              <p className="text-sm text-gray-500">{user.email} | ID: {user.registered_id}</p>
            </div>
            <div className="flex items-center space-x-4">
              <button
                onClick={refreshData}
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded transition-colors"
              >
                Refresh Data
              </button>
              <button
                onClick={handleLogout}
                className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded transition-colors"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-6 py-6">
        <div className="grid lg:grid-cols-4 gap-6">
          {/* Servers List */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-semibold text-gray-800 mb-4">Servers (Last 7 Days)</h2>
              
              {servers.length === 0 ? (
                <p className="text-gray-500 text-center py-8">No server activity in the last 7 days</p>
              ) : (
                <div className="space-y-3">
                  {servers.map((server) => (
                    <div
                      key={server.server_ip}
                      onClick={() => handleServerClick(server.server_ip)}
                      className={`p-4 rounded-lg cursor-pointer transition-all duration-200 ${
                        selectedServer === server.server_ip
                          ? 'bg-blue-100 border-2 border-blue-500'
                          : 'bg-gray-50 hover:bg-gray-100 border border-gray-200'
                      }`}
                    >
                      <div className="flex justify-between items-start mb-2">
                        <h3 className="font-semibold text-gray-800">{server.server_name}</h3>
                        <span className={`px-2 py-1 text-xs rounded-full ${
                          server.active_sessions > 0 
                            ? 'bg-green-100 text-green-800' 
                            : 'bg-gray-100 text-gray-600'
                        }`}>
                          {server.active_sessions > 0 ? 'Active' : 'Idle'}
                        </span>
                      </div>
                      <p className="text-sm text-gray-600 mb-1">{server.server_ip}</p>
                      <div className="text-xs text-gray-500 space-y-1">
                        <p>Sessions: {server.total_sessions}</p>
                        <p>Users: {server.unique_users_count}</p>
                        <p>Active: {server.active_sessions}</p>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Server Details */}
          <div className="lg:col-span-3">
            {selectedServer && serverDetails ? (
              <div className="space-y-6">
                {/* Server Stats */}
                <div className="bg-white rounded-lg shadow-md p-6">
                  <div className="flex justify-between items-center mb-4">
                    <h2 className="text-xl font-semibold text-gray-800">
                      Server Details: {serverDetails.server_ip}
                    </h2>
                    <div className="flex items-center space-x-4 text-sm text-gray-600">
                      <span>Total Sessions: {serverDetails.total_sessions}</span>
                      <span>Total Commands: {serverDetails.total_commands}</span>
                      <span>Active Now: {serverDetails.active_sessions.length}</span>
                    </div>
                  </div>

                  {/* Active Sessions Alert */}
                  {serverDetails.active_sessions.length > 0 && (
                    <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-4">
                      <h3 className="font-semibold text-green-800 mb-2">Currently Active Sessions:</h3>
                      <div className="space-y-2">
                        {serverDetails.active_sessions.map((session) => (
                          <div key={session._id} className="flex justify-between items-center text-sm">
                            <span className="text-green-700">
                              {session.user_id.name} ({session.user_id.registered_id})
                            </span>
                            <span className="text-green-600">
                              Connected: {formatTime(session.login_time)}
                            </span>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Tabs */}
                  <div className="border-b border-gray-200 mb-4">
                    <nav className="flex space-x-8">
                      <button
                        onClick={() => setActiveTab('sessions')}
                        className={`py-2 px-1 border-b-2 font-medium text-sm ${
                          activeTab === 'sessions'
                            ? 'border-blue-500 text-blue-600'
                            : 'border-transparent text-gray-500 hover:text-gray-700'
                        }`}
                      >
                        User Sessions ({serverDetails.sessions.length})
                      </button>
                      <button
                        onClick={() => setActiveTab('commands')}
                        className={`py-2 px-1 border-b-2 font-medium text-sm ${
                          activeTab === 'commands'
                            ? 'border-blue-500 text-blue-600'
                            : 'border-transparent text-gray-500 hover:text-gray-700'
                        }`}
                      >
                        Commands ({serverDetails.commands.length})
                      </button>
                      <button
                        onClick={() => setActiveTab('realtime')}
                        className={`py-2 px-1 border-b-2 font-medium text-sm ${
                          activeTab === 'realtime'
                            ? 'border-blue-500 text-blue-600'
                            : 'border-transparent text-gray-500 hover:text-gray-700'
                        }`}
                      >
                        Real-time Activity
                      </button>
                    </nav>
                  </div>

                  {/* Tab Content */}
                  <div className="max-h-96 overflow-y-auto">
                    {/* Sessions Tab */}
                    {activeTab === 'sessions' && (
                      <div className="space-y-4">
                        {serverDetails.sessions.length === 0 ? (
                          <p className="text-gray-500 text-center py-8">No sessions in the last 7 days</p>
                        ) : (
                          serverDetails.sessions.map((session) => (
                            <div key={session._id} className="border border-gray-200 rounded-lg p-4">
                              <div className="flex justify-between items-start mb-3">
                                <div>
                                  <h4 className="font-semibold text-gray-800">
                                    {session.user_id.name}
                                  </h4>
                                  <p className="text-sm text-gray-600">
                                    {session.user_id.email} | ID: {session.user_id.registered_id}
                                  </p>
                                </div>
                                <span className={`px-3 py-1 text-xs rounded-full ${
                                  session.status === 'active' 
                                    ? 'bg-green-100 text-green-800'
                                    : session.status === 'disconnected'
                                    ? 'bg-gray-100 text-gray-800'
                                    : 'bg-red-100 text-red-800'
                                }`}>
                                  {session.status}
                                </span>
                              </div>
                              
                              <div className="grid md:grid-cols-3 gap-4 text-sm">
                                <div>
                                  <span className="font-medium text-gray-700">Login:</span>
                                  <p className="text-gray-600">{formatTime(session.login_time)}</p>
                                </div>
                                <div>
                                  <span className="font-medium text-gray-700">Logout:</span>
                                  <p className="text-gray-600">
                                    {session.logout_time ? formatTime(session.logout_time) : 'Still active'}
                                  </p>
                                </div>
                                <div>
                                  <span className="font-medium text-gray-700">Duration:</span>
                                  <p className="text-gray-600">
                                    {session.session_duration > 0 ? formatDuration(session.session_duration) : 'Ongoing'}
                                  </p>
                                </div>
                              </div>
                              
                              <div className="mt-3 text-sm">
                                <span className="font-medium text-gray-700">SSH User:</span>
                                <span className="ml-2 text-gray-600">{session.ssh_username}</span>
                                <span className="ml-4 font-medium text-gray-700">Commands:</span>
                                <span className="ml-2 text-gray-600">{session.commands_executed.length}</span>
                              </div>
                            </div>
                          ))
                        )}
                      </div>
                    )}

                    {/* Commands Tab */}
                    {activeTab === 'commands' && (
                      <div className="space-y-3">
                        {serverDetails.commands.length === 0 ? (
                          <p className="text-gray-500 text-center py-8">No commands executed in the last 7 days</p>
                        ) : (
                          serverDetails.commands.map((command) => (
                            <div key={command._id} className="border border-gray-200 rounded-lg p-4">
                              <div className="flex justify-between items-start mb-2">
                                <div className="flex-1">
                                  <div className="flex items-center space-x-3 mb-1">
                                    <span className="font-semibold text-gray-800">
                                      {command.user_id.name}
                                    </span>
                                    <span className="text-sm text-gray-500">
                                      ({command.user_id.registered_id})
                                    </span>
                                    <span className="text-xs text-gray-400">
                                      {formatTime(command.executed_at)}
                                    </span>
                                  </div>
                                  
                                  <div className="bg-gray-900 text-green-400 p-3 rounded font-mono text-sm overflow-x-auto">
                                    $ {command.command}
                                  </div>
                                  
                                  {command.output && (
                                    <div className="mt-2 bg-gray-100 p-2 rounded text-sm max-h-32 overflow-y-auto">
                                      <span className="text-gray-600">Output:</span>
                                      <pre className="text-xs text-gray-700 whitespace-pre-wrap">
                                        {command.output.slice(0, 500)}
                                        {command.output.length > 500 && '...'}
                                      </pre>
                                    </div>
                                  )}
                                </div>
                                
                                <span className={`px-2 py-1 text-xs rounded-full ml-4 ${
                                  command.status === 'success' 
                                    ? 'bg-green-100 text-green-800'
                                    : command.status === 'error'
                                    ? 'bg-red-100 text-red-800'
                                    : 'bg-yellow-100 text-yellow-800'
                                }`}>
                                  {command.status}
                                </span>
                              </div>
                            </div>
                          ))
                        )}
                      </div>
                    )}

                    {/* Real-time Activity Tab */}
                    {activeTab === 'realtime' && (
                      <div className="space-y-3">
                        <div className="flex justify-between items-center mb-4">
                          <h3 className="text-lg font-semibold text-gray-800">Last 24 Hours Activity</h3>
                          <button
                            onClick={() => loadRealtimeActivity(selectedServer)}
                            className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded text-sm transition-colors"
                          >
                            Refresh
                          </button>
                        </div>
                        
                        {realtimeActivity.length === 0 ? (
                          <p className="text-gray-500 text-center py-8">No recent activity in the last 24 hours</p>
                        ) : (
                          realtimeActivity.map((activity) => (
                            <div key={activity._id} className="border border-gray-200 rounded-lg p-3 bg-gray-50">
                              <div className="flex justify-between items-center mb-2">
                                <span className="font-medium text-gray-800">
                                  {activity.user_id.name} ({activity.user_id.registered_id})
                                </span>
                                <span className="text-xs text-gray-500">
                                  {formatTime(activity.executed_at)}
                                </span>
                              </div>
                              <div className="bg-gray-900 text-green-400 p-2 rounded font-mono text-sm">
                                $ {activity.command}
                              </div>
                            </div>
                          ))
                        )}
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ) : (
              <div className="bg-white rounded-lg shadow-md p-6">
                <div className="text-center py-12">
                  <div className="text-gray-400 text-6xl mb-4">🖥️</div>
                  <h2 className="text-2xl font-semibold text-gray-600 mb-2">Select a Server</h2>
                  <p className="text-gray-500">
                    Click on a server from the left panel to view detailed usage information,
                    user sessions, and command history from the last 7 days.
                  </p>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Overview Stats */}
        <div className="mt-8 grid md:grid-cols-4 gap-4">
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-sm font-medium text-gray-600">Total Servers</h3>
            <p className="text-3xl font-bold text-blue-600">{servers.length}</p>
          </div>
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-sm font-medium text-gray-600">Active Sessions</h3>
            <p className="text-3xl font-bold text-green-600">
              {servers.reduce((sum, s) => sum + s.active_sessions, 0)}
            </p>
          </div>
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-sm font-medium text-gray-600">Total Sessions (7d)</h3>
            <p className="text-3xl font-bold text-purple-600">
              {servers.reduce((sum, s) => sum + s.total_sessions, 0)}
            </p>
          </div>
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-sm font-medium text-gray-600">Unique Users (7d)</h3>
            <p className="text-3xl font-bold text-orange-600">
              {servers.reduce((sum, s) => sum + s.unique_users_count, 0)}
            </p>
          </div>
        </div>
      </div>
      
      {loading && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg">
            <div className="flex items-center space-x-3">
              <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-600"></div>
              <span>Loading server details...</span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
