import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import { Terminal } from "xterm";
import "xterm/css/xterm.css";

const SERVER_URL = "http://localhost:5000";

const TerminalPage = () => {
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [connectionData, setConnectionData] = useState(null);
  const [error, setError] = useState("");
  const terminalRef = useRef();
  const terminalInstance = useRef();

  useEffect(() => {
    // Get connection data from URL parameters
    const urlParams = new URLSearchParams(window.location.search);
    const data = urlParams.get('data');
    
    if (data) {
      try {
        const parsedData = JSON.parse(decodeURIComponent(data));
        setConnectionData(parsedData);
      } catch (err) {
        setError("Invalid connection data");
        return;
      }
    } else {
      setError("No connection data provided");
      return;
    }

    // Initialize Socket.IO
    const newSocket = io(SERVER_URL);
    setSocket(newSocket);

    // Initialize Terminal
    const term = new Terminal({
      cursorBlink: true,
      theme: {
        background: '#1e1e1e',
        foreground: '#ffffff',
        cursor: '#ffffff',
        selection: '#ffffff40'
      },
      fontSize: 14,
      fontFamily: 'Consolas, Monaco, "Courier New", monospace'
    });

    term.open(terminalRef.current);
    terminalInstance.current = term;

    // Socket event listeners
    newSocket.on("connect", () => {
      console.log("Connected to server");
      setIsConnected(true);
    });

    newSocket.on("ssh-output", (data) => {
      term.write(data);
    });

    newSocket.on("disconnect", () => {
      setIsConnected(false);
      term.write("\r\n[Disconnected from server]\r\n");
    });

    newSocket.on("connect_error", (err) => {
      setError(`Connection error: ${err.message}`);
    });

    // Terminal input handler
    term.onData((input) => {
      if (newSocket && isConnected) {
        newSocket.emit("ssh-input", input);
      }
    });

    // Auto-connect when everything is ready
    if (parsedData) {
      setTimeout(() => {
        connectToServer(newSocket, parsedData);
      }, 1000);
    }

    // Cleanup on unmount
    return () => {
      if (newSocket) {
        newSocket.disconnect();
      }
      if (term) {
        term.dispose();
      }
    };
  }, []);

  const connectToServer = (socketInstance, data) => {
    if (!socketInstance || !data) return;

    socketInstance.emit("ssh-connect", {
      host: data.host,
      username: data.username,
      password: data.password,
      server_name: data.server_name,
      user_data: data.user_data // Pass user data for session tracking
    });

    // Show connection attempt
    if (terminalInstance.current) {
      terminalInstance.current.write(`Connecting to ${data.server_name} (${data.host})...\r\n`);
      terminalInstance.current.write(`User: ${data.user_data.name} (${data.user_data.registered_id})\r\n`);
      terminalInstance.current.write(`SSH Username: ${data.username}\r\n`);
      terminalInstance.current.write("Please wait...\r\n\r\n");
    }
  };

  const handleDisconnect = () => {
    if (socket) {
      socket.disconnect();
    }
    window.close();
  };

  const handleNewTerminal = () => {
    if (terminalInstance.current) {
      terminalInstance.current.clear();
    }
    if (socket && connectionData) {
      connectToServer(socket, connectionData);
    }
  };

  if (error) {
    return (
      <div className="min-h-screen bg-red-50 flex items-center justify-center">
        <div className="bg-white p-8 rounded-lg shadow-lg max-w-md w-full">
          <div className="text-center">
            <div className="text-red-500 text-6xl mb-4">⚠️</div>
            <h2 className="text-2xl font-bold text-gray-800 mb-2">Connection Error</h2>
            <p className="text-gray-600 mb-4">{error}</p>
            <button
              onClick={() => window.close()}
              className="bg-gray-500 hover:bg-gray-600 text-white px-6 py-2 rounded transition-colors"
            >
              Close Window
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 flex flex-col">
      {/* Header */}
      <div className="bg-gray-800 text-white p-4 flex justify-between items-center">
        <div className="flex items-center space-x-4">
          <h1 className="text-xl font-bold">
            {connectionData?.server_name || "Terminal"} - {connectionData?.host}
          </h1>
          <div className="flex items-center space-x-2">
            <div className={`w-3 h-3 rounded-full ${isConnected ? 'bg-green-400' : 'bg-red-400'}`}></div>
            <span className="text-sm">
              {isConnected ? 'Connected' : 'Disconnected'}
            </span>
          </div>
        </div>
        
        <div className="flex items-center space-x-2">
          {connectionData?.user_data && (
            <span className="text-sm text-gray-300">
              User: {connectionData.user_data.name} ({connectionData.user_data.registered_id})
            </span>
          )}
        </div>

        <div className="flex items-center space-x-2">
          <button
            onClick={handleNewTerminal}
            className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded text-sm transition-colors"
          >
            New Session
          </button>
          <button
            onClick={handleDisconnect}
            className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded text-sm transition-colors"
          >
            Disconnect
          </button>
        </div>
      </div>

      {/* Terminal Container */}
      <div className="flex-1 p-4">
        <div className="bg-black rounded-lg p-4 h-full">
          <div
            ref={terminalRef}
            className="w-full h-full"
            style={{ minHeight: "600px" }}
          />
        </div>
      </div>

      {/* Footer */}
      <div className="bg-gray-800 text-gray-400 p-2 text-center text-sm">
        Terminal Session - {connectionData?.server_name} | User: {connectionData?.user_data?.name}
      </div>
    </div>
  );
};

export default TerminalPage;
