import React, { useState, useEffect, useRef, useCallback } from "react";
import { io } from "socket.io-client";
import { Terminal } from "xterm";
import "xterm/css/xterm.css";
import { FitAddon } from 'xterm-addon-fit';

const SERVER_URL = "http://localhost:5000";

const TerminalPage = () => {
  const [isConnected, setIsConnected] = useState(false);
  const [connectionData, setConnectionData] = useState(null);
  const [error, setError] = useState("");
  const [connectionStatus, setConnectionStatus] = useState("initializing");
  
  // Refs that don't cause re-renders
  const terminalRef = useRef();
  const terminalInstance = useRef();
  const socketRef = useRef();
  const fitAddonRef = useRef(); // Move fitAddon to ref to prevent recreation
  const isInitializedRef = useRef(false); // Prevent multiple initializations

  const getStoredUserData = useCallback(() => {
    try {
      const userData = localStorage.getItem('user');
      const token = localStorage.getItem('token');
      
      if (userData) {
        const user = JSON.parse(userData);
        return {
          token: token || '',
          userId: user.id || '',
          userEmail: user.email || '',
          userName: user.name || '',
          registeredId: user.registered_id || '',
          userRole: user.role || 'student'
        };
      }
    } catch (err) {
      console.warn("Could not retrieve stored user data:", err);
    }
    return {};
  }, []);

  const connectToSSHServer = useCallback((socketInstance, data, term) => {
    if (!socketInstance || !data) {
      console.error("Missing socket instance or connection data");
      term.write(`âŒ Cannot connect: Missing connection data\r\n`);
      return;
    }

    console.log("Attempting SSH connection with data:", data);

    const sshConnectionData = {
      host: data.host,
      username: data.username,
      password: data.password,
      port: data.port || 22,
      
      user: {
        id: data.userId,
        name: data.userName,
        email: data.userEmail,
        registered_id: data.registeredId,
        role: data.userRole
      },
      
      session: {
        id: data.sessionId,
        token: data.token,
        connectionTime: data.connectionTime,
        serverName: data.serverName,
        dashboardUrl: data.dashboardUrl
      },
      
      browser: {
        userAgent: data.browserInfo || navigator.userAgent,
        windowId: window.name,
        referrer: document.referrer
      }
    };

    term.write(`ðŸ”— Connecting to SSH server...\r\n`);
    term.write(`ðŸ“¡ Host: ${data.host}:${data.port || 22}\r\n`);
    term.write(`ðŸ‘¤ User: ${data.username}\r\n`);
    term.write(`ðŸ” Authenticating...\r\n\r\n`);

    console.log("Sending SSH connection data:", sshConnectionData);

    socketInstance.emit("ssh-connect", sshConnectionData);
    socketInstance.emit("ssh-connect-enhanced", sshConnectionData);
    socketInstance.emit("connect-ssh", sshConnectionData);
    
    socketInstance.emit("user-activity", {
      type: 'terminal_access',
      userId: data.userId,
      userName: data.userName,
      action: 'ssh_connect_attempt',
      target: data.host,
      timestamp: new Date().toISOString(),
      sessionId: data.sessionId
    });

    const timeoutId = setTimeout(() => {
      if (connectionStatus === "connecting" || connectionStatus === "connected") {
        term.write(`â±ï¸ Connection is taking longer than expected...\r\n`);
        term.write(`ðŸ’¡ Check if your backend server is handling SSH connections properly\r\n\r\n`);
      }
    }, 10000);

    // Store timeout ID to clear it later if needed
    return timeoutId;
  }, []); // Remove connectionStatus dependency

  const cleanupConnection = useCallback(() => {
    console.log("Cleaning up connection...");
    
    if (socketRef.current) {
      socketRef.current.removeAllListeners(); // Remove all listeners first
      socketRef.current.disconnect();
      socketRef.current = null;
    }
    
    if (terminalInstance.current) {
      terminalInstance.current.dispose();
      terminalInstance.current = null;
    }
    
    // Clean up session storage
    Object.keys(sessionStorage).forEach(key => {
      if (key.startsWith('terminal_')) {
        sessionStorage.removeItem(key);
      }
    });
    
    // Reset initialization flag
    isInitializedRef.current = false;
  }, []);

  const initializeTerminal = useCallback((data) => {
    // Prevent multiple initializations
    if (isInitializedRef.current) {
      console.log("Terminal already initialized, skipping...");
      return;
    }
    
    console.log("Initializing terminal with data:", data);
    isInitializedRef.current = true;
    setConnectionStatus("connecting");
    
    // Create fitAddon only once
    if (!fitAddonRef.current) {
      fitAddonRef.current = new FitAddon();
    }
    
    // Initialize Terminal
    const term = new Terminal({
      cursorBlink: true,
      theme: {
        background: '#000000',
        foreground: '#ffffff',
        cursor: '#ffffff',
        selection: '#ffffff40',
        black: '#000000',
        red: '#e74c3c',
        green: '#2ecc71',
        yellow: '#f1c40f',
        blue: '#3498db',
        magenta: '#9b59b6',
        cyan: '#1abc9c',
        white: '#ecf0f1'
      },
      fontSize: 14,
      fontFamily: 'Consolas, Monaco, "Courier New", monospace',
      rows: 30,
      cols: 120,
      scrollback: 1000,
      convertEol: true
    });

    term.loadAddon(fitAddonRef.current);

    if (terminalRef.current) {
      term.open(terminalRef.current);
      fitAddonRef.current.fit();
      terminalInstance.current = term;
      
      term.write('ðŸš€ Initializing terminal...\r\n');
      term.write(`ðŸ“¡ Connecting to ${SERVER_URL}...\r\n\r\n`);
    }
    
    // Clean up existing socket before creating new one
    if (socketRef.current) {
      socketRef.current.removeAllListeners();
      socketRef.current.disconnect();
    }
    
    // Initialize Socket.IO
    const newSocket = io(SERVER_URL, {
      query: {
        userId: data.userId || '',
        userName: data.userName || '',
        userRole: data.userRole || 'student'
      },
      timeout: 10000,
      forceNew: true,
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000
    });
    
    socketRef.current = newSocket;

    // Socket event listeners
    newSocket.on("connect", () => {
      console.log("Connected to terminal server");
      setIsConnected(true);
      setConnectionStatus("connected");
      term.write(`âœ… Socket connected to ${SERVER_URL}\r\n`);
      term.write(`ðŸ”— Establishing SSH connection...\r\n\r\n`);
      
      connectToSSHServer(newSocket, data, term);
    });

    newSocket.on("ssh-output", (outputData) => {
      if (term && !term.isDisposed) {
        term.write(outputData);
      }
    });

    newSocket.on("ssh-connected", (info) => {
      setConnectionStatus("ssh-connected");
      setError("");
      term.write(`\r\nðŸŽ‰ SSH CONNECTION ESTABLISHED! ðŸŽ‰\r\n`);
      term.write(`âœ… Connected to: ${info.host || data.host}\r\n`);
      term.write(`ðŸ‘¤ User: ${data.userName || 'Unknown'}\r\n`);
      term.write(`ðŸ†” ID: ${data.registeredId || 'No ID'}\r\n`);
      term.write(`â° Time: ${new Date().toLocaleString()}\r\n`);
      term.write(`${'='.repeat(50)}\r\n`);
      term.write(`ðŸ’¡ You can now run commands like: pwd, ls, whoami\r\n`);
      term.write(`${'='.repeat(50)}\r\n\r\n`);
    });

    newSocket.on("ssh-error", (errorData) => {
      const errorMsg = errorData.message || errorData;
      term.write(`\r\nâŒ SSH Connection Error: ${errorMsg}\r\n`);
      term.write(`\r\nðŸ”„ Retrying connection in 3 seconds...\r\n`);
      setConnectionStatus("error");
      setError(errorMsg);
      
      setTimeout(() => {
        if (socketRef.current && data) {
          connectToSSHServer(socketRef.current, data, term);
        }
      }, 3000);
    });

    newSocket.on("ssh-disconnected", () => {
      term.write("\r\nðŸ”Œ SSH connection closed\r\n");
      setConnectionStatus("disconnected");
      setIsConnected(false);
    });

    newSocket.on("disconnect", (reason) => {
      setIsConnected(false);
      setConnectionStatus("disconnected");
      term.write(`\r\n[Socket disconnected: ${reason}]\r\n`);
      
      if (reason !== 'io client disconnect') {
        setTimeout(() => {
          if (socketRef.current && !socketRef.current.connected) {
            term.write(`\r\nðŸ”„ Attempting to reconnect...\r\n`);
            socketRef.current.connect();
          }
        }, 2000);
      }
    });

    newSocket.on("connect_error", (err) => {
      const errorMsg = `Connection error: ${err.message}`;
      setError(errorMsg);
      setConnectionStatus("error");
      term.write(`\r\nâŒ ${errorMsg}\r\n`);
      term.write(`\r\nðŸ’¡ Make sure your backend server is running on ${SERVER_URL}\r\n`);
    });

    // Terminal input handler
    term.onData((input) => {
      console.log("Terminal input:", input);
      if (newSocket && newSocket.connected) {
        newSocket.emit("ssh-input", input);
        console.log("Sent to server:", input);
      } else {
        term.write(`\r\nâŒ Not connected to server. Cannot send: ${input}\r\n`);
      }
    });

    term.onKey(({ key, domEvent }) => {
      if (domEvent.ctrlKey && domEvent.key === 'c') {
        if (newSocket && newSocket.connected) {
          newSocket.emit("ssh-input", '\x03');
        }
      }
    });

    // Handle window resize - using ref to prevent recreation
    const handleResize = () => {
      if (term && term.element && fitAddonRef.current && !term.isDisposed) {
        try {
          fitAddonRef.current.fit();
        } catch (fitError) {
          console.warn("Fit error:", fitError);
          // Fallback resize logic
          if (terminalRef.current) {
            const cols = Math.floor(terminalRef.current.clientWidth / 9);
            const rows = Math.floor(terminalRef.current.clientHeight / 17);
            term.resize(cols, rows);
          }
        }
      }
    };
    
    window.addEventListener('resize', handleResize);

    // Return cleanup function
    return () => {
      window.removeEventListener('resize', handleResize);
      if (newSocket) {
        newSocket.removeAllListeners();
        newSocket.disconnect();
      }
      if (term && !term.isDisposed) {
        term.dispose();
      }
    };
  }, [connectToSSHServer]); // Only depend on connectToSSHServer

  const initializeConnection = useCallback(() => {
    // Prevent multiple initializations
    if (isInitializedRef.current) {
      console.log("Connection already initialized, skipping...");
      return;
    }
    
    try {
      const urlParams = new URLSearchParams(window.location.search);
      let data = null;

      // Check for sessionKey method first
      const sessionKey = urlParams.get('sessionKey');
      if (sessionKey) {
        const sessionData = sessionStorage.getItem(sessionKey);
        if (sessionData) {
          data = JSON.parse(sessionData);
          sessionStorage.removeItem(sessionKey);
        }
      }

      // Fallback to direct URL data
      if (!data) {
        const urlData = urlParams.get('data') || urlParams.get('fallbackData');
        if (urlData) {
          data = JSON.parse(decodeURIComponent(urlData));
        }
      }

      // Old format compatibility
      if (!data) {
        const host = urlParams.get('host');
        const username = urlParams.get('username');
        const password = urlParams.get('password');
        const serverName = urlParams.get('serverName');
        const token = urlParams.get('token');
        const userId = urlParams.get('userId');

        if (host && username && password) {
          data = {
            host,
            username,
            password,
            serverName,
            token,
            userId,
            ...getStoredUserData()
          };
        }
      }

      // SessionStorage fallback
      if (!data) {
        const stored = sessionStorage.getItem('terminalConnection');
        if (stored) {
          data = JSON.parse(stored);
        }
      }

      if (data) {
        console.log("Setting connection data:", data);
        setConnectionData(data);
        // Don't call initializeTerminal here - let useEffect handle it
      } else {
        setError("No connection data provided. Please return to the dashboard and try again.");
      }
    } catch (err) {
      console.error("Error initializing connection:", err);
      setError("Failed to initialize connection data");
    }
  }, [getStoredUserData]);

  // Separate useEffect for initial connection data loading
  useEffect(() => {
    if (!isInitializedRef.current) {
      console.log("Loading initial connection data...");
      initializeConnection();
    }
  }, []); // Empty dependency array - run only once

  // Separate useEffect for terminal initialization when connectionData changes
  useEffect(() => {
    if (connectionData && !isInitializedRef.current) {
      console.log("Initializing terminal with connection data...");
      const cleanup = initializeTerminal(connectionData);
      
      return cleanup; // Return cleanup function
    }
  }, [connectionData, initializeTerminal]);

  // Handle postMessage communication
  useEffect(() => {
    const handleMessage = (event) => {
      if (event.data && event.data.type === 'TERMINAL_CONNECTION_DATA') {
        if (!isInitializedRef.current) {
          setConnectionData(event.data.data);
        }
      }
    };
    
    window.addEventListener('message', handleMessage);
    
    return () => {
      window.removeEventListener('message', handleMessage);
    };
  }, []); // Empty dependency array

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      console.log("Component unmounting, cleaning up...");
      cleanupConnection();
    };
  }, [cleanupConnection]);

  const handleDisconnect = useCallback(() => {
    if (socketRef.current) {
      socketRef.current.emit("user-disconnect", {
        userId: connectionData?.userId,
        userName: connectionData?.userName,
        sessionId: connectionData?.sessionId,
        disconnectTime: new Date().toISOString(),
        disconnectReason: 'user_initiated'
      });
      
      socketRef.current.disconnect();
    }
    
    cleanupConnection();
    window.close();
  }, [connectionData, cleanupConnection]);

  const handleReconnect = useCallback(() => {
    if (connectionData) {
      setError("");
      setConnectionStatus("reconnecting");
      
      // Reset initialization flag to allow reconnection
      isInitializedRef.current = false;
      
      if (terminalInstance.current && !terminalInstance.current.isDisposed) {
        terminalInstance.current.clear();
        terminalInstance.current.write("ðŸ”„ Reconnecting...\r\n");
      }
      
      // Clean up existing connection
      if (socketRef.current) {
        socketRef.current.removeAllListeners();
        socketRef.current.disconnect();
        socketRef.current = null;
      }
      
      if (terminalInstance.current) {
        terminalInstance.current.dispose();
        terminalInstance.current = null;
      }
      
      // Reinitialize after cleanup
      setTimeout(() => {
        initializeTerminal(connectionData);
      }, 1000);
    }
  }, [connectionData, initializeTerminal]);

  const handleNewSession = useCallback(() => {
    if (terminalInstance.current && !terminalInstance.current.isDisposed) {
      terminalInstance.current.clear();
    }
    if (socketRef.current && connectionData) {
      const newSessionData = {
        ...connectionData,
        sessionId: `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        connectionTime: new Date().toISOString()
      };
      setConnectionData(newSessionData);
      connectToSSHServer(socketRef.current, newSessionData, terminalInstance.current);
    }
  }, [connectionData, connectToSSHServer]);

  const handleNewTerminal = useCallback(() => {
    handleNewSession();
  }, [handleNewSession]);

  const getStatusColor = () => {
    switch (connectionStatus) {
      case "connected":
      case "ssh-connected":
        return "#2ecc71";
      case "connecting":
      case "reconnecting":
        return "#f39c12";
      case "error":
        return "#e74c3c";
      default:
        return "#95a5a6";
    }
  };

  const getStatusText = () => {
    switch (connectionStatus) {
      case "initializing":
        return "Initializing...";
      case "connecting":
        return "Connecting...";
      case "connected":
        return "Socket Connected";
      case "ssh-connected":
        return "SSH Connected";
      case "reconnecting":
        return "Reconnecting...";
      case "disconnected":
        return "Disconnected";
      case "error":
        return "Connection Error";
      default:
        return "Unknown";
    }
  };

  const getUserDisplayInfo = () => {
    if (!connectionData) return { name: 'Unknown', id: 'N/A', email: 'N/A' };
    
    if (connectionData.userName) {
      return {
        name: connectionData.userName,
        id: connectionData.registeredId || 'N/A',
        email: connectionData.userEmail || 'N/A',
        role: connectionData.userRole || 'student'
      };
    }
    
    if (connectionData.user_data) {
      return {
        name: connectionData.user_data.name || 'Unknown',
        id: connectionData.user_data.registered_id || 'N/A',
        email: connectionData.user_data.email || 'N/A',
        role: connectionData.user_data.role || 'student'
      };
    }
    
    return { name: 'Unknown', id: 'N/A', email: 'N/A', role: 'student' };
  };

  if (error && !connectionData) {
    return (
      <div style={{ 
        height: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        backgroundColor: '#2c3e50',
        color: 'white',
        fontFamily: 'Arial, sans-serif'
      }}>
        <div style={{ 
          textAlign: 'center', 
          padding: '40px',
          backgroundColor: '#34495e',
          borderRadius: '8px',
          maxWidth: '500px'
        }}>
          <div style={{ fontSize: '48px', marginBottom: '20px' }}>âš ï¸</div>
          <h2 style={{ margin: '0 0 15px 0' }}>Terminal Connection Error</h2>
          <p style={{ margin: '0 0 20px 0', color: '#bdc3c7' }}>{error}</p>
          <p style={{ margin: '0 0 20px 0', fontSize: '14px', color: '#95a5a6' }}>
            Please return to the dashboard and try connecting again.
          </p>
          <button 
            onClick={() => window.close()}
            style={{
              padding: '10px 20px',
              backgroundColor: '#e74c3c',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '16px'
            }}
          >
            Close Window
          </button>
        </div>
      </div>
    );
  }

  if (!connectionData) {
    return (
      <div style={{ 
        height: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        backgroundColor: '#2c3e50',
        color: 'white',
        fontFamily: 'Arial, sans-serif'
      }}>
        <div style={{ 
          textAlign: 'center', 
          padding: '40px',
          backgroundColor: '#34495e',
          borderRadius: '8px',
          maxWidth: '500px'
        }}>
          <div style={{ fontSize: '48px', marginBottom: '20px' }}>ðŸ”„</div>
          <h2 style={{ margin: '0 0 15px 0' }}>Loading Terminal...</h2>
          <p style={{ margin: '0 0 20px 0', color: '#bdc3c7' }}>Initializing connection data...</p>
          <div style={{ marginTop: '20px' }}>
            <button 
              onClick={handleReconnect}
              style={{
                padding: '10px 20px',
                backgroundColor: '#3498db',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '14px'
              }}
            >
              ðŸ”„ Retry Connection
            </button>
          </div>
        </div>
      </div>
    );
  }

  const userInfo = getUserDisplayInfo();

  return (
    <div style={{ height: '100vh', display: 'flex', flexDirection: 'column', backgroundColor: '#2c3e50' }}>
      {/* Enhanced Header with User Info */}
      <div style={{ 
        padding: '12px 20px', 
        backgroundColor: '#34495e', 
        color: 'white',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        borderBottom: '2px solid #2c3e50',
        minHeight: '60px'
      }}>
        <div style={{ flex: 1 }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
            <h3 style={{ margin: 0, fontSize: '18px', fontWeight: 'bold' }}>
              ðŸ–¥ï¸ {connectionData?.serverName || connectionData?.host || "Terminal"}
            </h3>
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '6px',
              padding: '4px 8px',
              backgroundColor: getStatusColor(),
              borderRadius: '12px',
              fontSize: '12px'
            }}>
              <div style={{ 
                width: '8px', 
                height: '8px', 
                borderRadius: '50%', 
                backgroundColor: 'white' 
              }}></div>
              {getStatusText()}
            </div>
          </div>
          
          <div style={{ fontSize: '13px', color: '#bdc3c7', marginTop: '4px' }}>
            ðŸ‘¤ {userInfo.name} 
            {userInfo.id !== 'N/A' && ` (ID: ${userInfo.id})`}
            {userInfo.email !== 'N/A' && ` | ðŸ“§ ${userInfo.email}`}
            {connectionData && ` | ðŸ”— ${connectionData.username}@${connectionData.host}`}
          </div>
        </div>

        <div style={{ display: 'flex', gap: '8px' }}>
          {(connectionStatus === "error" || connectionStatus === "disconnected") && (
            <button 
              onClick={handleReconnect}
              style={{
                padding: '6px 12px',
                backgroundColor: '#f39c12',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '12px'
              }}
            >
              ðŸ”„ Reconnect
            </button>
          )}
          
          <button 
            onClick={handleNewTerminal}
            style={{
              padding: '6px 12px',
              backgroundColor: '#3498db',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '12px'
            }}
          >
            ðŸ†• New Session
          </button>
          
          <button 
            onClick={handleDisconnect}
            style={{
              padding: '6px 12px',
              backgroundColor: '#e74c3c',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '12px'
            }}
          >
            âŒ Disconnect & Close
          </button>
        </div>
      </div>

      {/* Connection Info Bar */}
      {connectionData && (
        <div style={{ 
          padding: '8px 20px', 
          backgroundColor: '#2c3e50', 
          color: '#ecf0f1',
          fontSize: '12px',
          borderBottom: '1px solid #34495e'
        }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
              ðŸŒ Server: {connectionData.host} | 
              ðŸ‘¥ Role: {userInfo.role} | 
              ðŸ”‘ Session: {connectionData.sessionId?.substr(-8) || 'N/A'}
            </div>
            <div>
              {connectionData.connectionTime && (
                <>Connected: {new Date(connectionData.connectionTime).toLocaleTimeString()}</>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Debug Info Bar */}
      <div style={{ 
        padding: '4px 20px', 
        backgroundColor: '#1a252f', 
        color: '#95a5a6',
        fontSize: '11px',
        borderBottom: '1px solid #34495e'
      }}>
        ðŸ” Debug: Socket URL: {SERVER_URL} | Status: {connectionStatus} | 
        Connected: {isConnected ? 'Yes' : 'No'} | 
        {error && `Error: ${error}`}
      </div>

      {/* Terminal Container */}
      <div 
        ref={terminalRef} 
        style={{ 
          flex: 1, 
          padding: '10px',
          backgroundColor: '#000000',
          overflow: 'hidden',
          border: '1px solid #34495e',
          margin: '5px'
        }} 
      />

      {/* Status Footer */}
      <div style={{ 
        padding: '8px 20px', 
        backgroundColor: '#34495e', 
        color: '#bdc3c7',
        fontSize: '11px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <div>
          {error ? (
            <span style={{ color: '#e74c3c' }}>âŒ Error: {error}</span>
          ) : (
            <span>
              Terminal ready | Use Ctrl+C to interrupt | Type 'exit' to close SSH session
            </span>
          )}
        </div>
        <div>
          {userInfo.name !== 'Unknown' && `Logged in as: ${userInfo.name}`}
        </div>
      </div>
    </div>
  );
};

export default TerminalPage;
