import React, { useEffect, useRef, useState } from "react";
import { io } from "socket.io-client";
import { Terminal } from "xterm";
import { FitAddon } from "xterm-addon-fit";
import "xterm/css/xterm.css";

// ✅ Allowed servers list
const allowedServers = [
  {
    host: "192.168.1.100",
    username: "test",
    password: "1234",
    description: "Extra Testing Server",
  },
  {
    host: "172.184.216.215",
    username: "student1",
    password: "password123",
    description: "Internship Management Project",
  },
];

export default function StudentDashboard() {
  const termRef = useRef(null);
  const socketRef = useRef(null);
  const terminalInstance = useRef(null);
  const fitAddonRef = useRef(null);

  const [host, setHost] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
    if (!termRef.current) return;

    const term = new Terminal({ cursorBlink: true });
    const fitAddon = new FitAddon();
    terminalInstance.current = term;
    fitAddonRef.current = fitAddon;

    term.loadAddon(fitAddon);
    term.open(termRef.current);

    setTimeout(() => {
      fitAddon.fit();
    }, 50);

    const handleResize = () => fitAddon.fit();
    window.addEventListener("resize", handleResize);

    const socket = io("http://localhost:5000");
    socketRef.current = socket;

    socket.on("ssh-output", (data) => term.write(data));
    term.onData((data) => socket.emit("ssh-input", data));

    return () => {
      socket.disconnect();
      term.dispose();
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const handleConnect = () => {
    const match = allowedServers.find(
      (srv) =>
        srv.host === host &&
        srv.username === username &&
        srv.password === password
    );

    if (!match) {
      alert("❌ Access denied: Server not registered in the system.");
      return;
    }

    if (socketRef.current) {
      socketRef.current.emit("ssh-connect", {
        host: match.host,
        username: match.username,
        password: match.password,
      });
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Student Dashboard - Connect to Your Server</h1>

      <div style={{ marginBottom: "10px" }}>
        <input
          type="text"
          placeholder="Server IP / Address"
          value={host}
          onChange={(e) => setHost(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <button onClick={handleConnect}>Connect</button>
      </div>

      <div
        ref={termRef}
        style={{
          height: "500px",
          width: "100%",
          background: "black",
          border: "1px solid #ccc",
        }}
      />
    </div>
  );
}
