import React, { useState, useEffect } from 'react';
import './StudentManagement.css';

const StudentManagement = ({ onBack }) => {
  const [students, setStudents] = useState([]);
  const [allowedEmails, setAllowedEmails] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('registered'); // 'registered' or 'allowed'
  const [showAddModal, setShowAddModal] = useState(false);
  const [addStudentForm, setAddStudentForm] = useState({
    name: '',
    email: '',
    registered_id: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('adminToken');
      
      // Fetch registered students
      const studentsResponse = await fetch('/api/admin/students', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      if (studentsResponse.ok) {
        const studentsData = await studentsResponse.json();
        setStudents(studentsData);
      }

      // Fetch allowed emails
      const allowedResponse = await fetch('/api/admin/allowed-emails', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      if (allowedResponse.ok) {
        const allowedData = await allowedResponse.json();
        setAllowedEmails(allowedData);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to fetch student data');
    } finally {
      setLoading(false);
    }
  };

  const formatDuration = (seconds) => {
    if (!seconds) return '0s';
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    
    if (hours > 0) return `${hours}h ${minutes}m ${secs}s`;
    if (minutes > 0) return `${minutes}m ${secs}s`;
    return `${secs}s`;
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Never';
    return new Date(dateString).toLocaleString();
  };

  const handleAddStudent = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const token = localStorage.getItem('adminToken');
      const response = await fetch('/api/admin/students/allowed', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({
          ...addStudentForm,
          role: 'student'
        })
      });

      const data = await response.json();

      if (response.ok) {
        setSuccess('Student added successfully to allowed list');
        setAddStudentForm({ name: '', email: '', registered_id: '' });
        setShowAddModal(false);
        fetchData(); // Refresh data
      } else {
        setError(data.message || 'Failed to add student');
      }
    } catch (error) {
      console.error('Error adding student:', error);
      setError('Failed to add student');
    }
  };

  const handleDeleteAllowedEmail = async (emailId, studentName) => {
    if (!window.confirm(`Are you sure you want to remove ${studentName} from the allowed list?`)) {
      return;
    }

    try {
      const token = localStorage.getItem('adminToken');
      const response = await fetch(`/api/admin/students/allowed/${emailId}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });

      if (response.ok) {
        setSuccess('Student removed from allowed list successfully');
        fetchData(); // Refresh data
      } else {
        const data = await response.json();
        setError(data.message || 'Failed to remove student');
      }
    } catch (error) {
      console.error('Error removing student:', error);
      setError('Failed to remove student');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setAddStudentForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  if (loading) {
    return (
      <div className="student-management">
        <div className="loading">Loading student data...</div>
      </div>
    );
  }

  return (
    <div className="student-management">
      {/* Header */}
      <div className="header">
        <button onClick={onBack} className="back-button">
          ← Back to Dashboard
        </button>
        <h1>Student Management</h1>
        <button 
          onClick={() => setShowAddModal(true)}
          className="add-student-button"
        >
          + Add Student
        </button>
      </div>

      {/* Messages */}
      {error && <div className="message error">{error}</div>}
      {success && <div className="message success">{success}</div>}

      {/* Tabs */}
      <div className="tabs">
        <button 
          className={`tab ${activeTab === 'registered' ? 'active' : ''}`}
          onClick={() => setActiveTab('registered')}
        >
          Registered Students ({students.length})
        </button>
        <button 
          className={`tab ${activeTab === 'allowed' ? 'active' : ''}`}
          onClick={() => setActiveTab('allowed')}
        >
          Allowed Emails ({allowedEmails.length})
        </button>
      </div>

      {/* Registered Students Tab */}
      {activeTab === 'registered' && (
        <div className="content">
          <h2>Registered Students ({students.length})</h2>
          {students.length === 0 ? (
            <div className="no-data">No registered students found</div>
          ) : (
            <div className="table-container">
              <table className="students-table">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Student ID</th>
                    <th>Total Sessions</th>
                    <th>Active Sessions</th>
                    <th>Total Time</th>
                    <th>Last Activity</th>
                  </tr>
                </thead>
                <tbody>
                  {students.map(student => (
                    <tr key={student.id}>
                      <td className="name-cell">
                        <strong>{student.name}</strong>
                      </td>
                      <td>{student.email}</td>
                      <td>{student.registered_id}</td>
                      <td>{student.statistics.total_sessions}</td>
                      <td>
                        <span className={`status ${student.statistics.active_sessions > 0 ? 'active' : 'inactive'}`}>
                          {student.statistics.active_sessions}
                        </span>
                      </td>
                      <td>{formatDuration(student.statistics.total_duration)}</td>
                      <td>{formatDate(student.statistics.last_activity)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}

      {/* Allowed Emails Tab */}
      {activeTab === 'allowed' && (
        <div className="content">
          <h2>Allowed Emails ({allowedEmails.length})</h2>
          {allowedEmails.length === 0 ? (
            <div className="no-data">No allowed emails found</div>
          ) : (
            <div className="table-container">
              <table className="allowed-emails-table">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Student ID</th>
                    <th>Role</th>
                    <th>Added Date</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {allowedEmails.map(email => (
                    <tr key={email._id}>
                      <td>{email.name || 'N/A'}</td>
                      <td>{email.email}</td>
                      <td>{email.registered_id || 'N/A'}</td>
                      <td>
                        <span className={`role-badge ${email.role}`}>
                          {email.role}
                        </span>
                      </td>
                      <td>{formatDate(email.createdAt)}</td>
                      <td>
                        <button
                          onClick={() => handleDeleteAllowedEmail(email._id, email.name || email.email)}
                          className="delete-button"
                        >
                          Remove
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}

      {/* Add Student Modal */}
      {showAddModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>Add New Student</h3>
              <button 
                onClick={() => setShowAddModal(false)}
                className="close-button"
              >
                ×
              </button>
            </div>
            <form onSubmit={handleAddStudent}>
              <div className="form-group">
                <label htmlFor="name">Student Name *</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={addStudentForm.name}
                  onChange={handleInputChange}
                  required
                  placeholder="Enter student's full name"
                />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email Address *</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={addStudentForm.email}
                  onChange={handleInputChange}
                  required
                  placeholder="Enter student's email address"
                />
              </div>
              <div className="form-group">
                <label htmlFor="registered_id">Student ID *</label>
                <input
                  type="text"
                  id="registered_id"
                  name="registered_id"
                  value={addStudentForm.registered_id}
                  onChange={handleInputChange}
                  required
                  placeholder="Enter student's registration ID"
                />
              </div>
              <div className="modal-actions">
                <button 
                  type="button" 
                  onClick={() => setShowAddModal(false)}
                  className="cancel-button"
                >
                  Cancel
                </button>
                <button type="submit" className="submit-button">
                  Add Student
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default StudentManagement;
