import React, { useState, useEffect } from "react";
import "./AdminDashboard.css"; // Import the CSS file

const AdminDashboard = () => {
  const [user, setUser] = useState(null);
  const [servers, setServers] = useState([]);
  const [selectedServer, setSelectedServer] = useState(null);
  const [serverDetails, setServerDetails] = useState(null);
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState("sessions"); // sessions, commands, realtime
  const [realtimeActivity, setRealtimeActivity] = useState([]);

  useEffect(() => {
    const userData = localStorage.getItem("user");
    if (userData) {
      setUser(JSON.parse(userData));
      loadServers();
    }
  }, []);

  const loadServers = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch("http://localhost:5000/api/admin/servers", {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (response.ok) {
        const data = await response.json();
        setServers(data.servers);
      }
    } catch (error) {
      console.error("Error loading servers:", error);
    }
  };

  const loadServerDetails = async (serverIp) => {
    setLoading(true);
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `http://localhost:5000/api/admin/servers/${serverIp}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (response.ok) {
        const data = await response.json();
        setServerDetails(data);
        setSelectedServer(serverIp);
      }
    } catch (error) {
      console.error("Error loading server details:", error);
    }
    setLoading(false);
  };

  const loadRealtimeActivity = async (serverIp) => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `http://localhost:5000/api/admin/servers/${serverIp}/realtime`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (response.ok) {
        const data = await response.json();
        setRealtimeActivity(data.recent_activity);
      }
    } catch (error) {
      console.error("Error loading realtime activity:", error);
    }
  };

  const formatDuration = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    if (hours > 0) return `${hours}h ${minutes}m ${secs}s`;
    if (minutes > 0) return `${minutes}m ${secs}s`;
    return `${secs}s`;
  };

  const formatTime = (dateString) => new Date(dateString).toLocaleString();

  const handleServerClick = (serverIp) => {
    loadServerDetails(serverIp);
    loadRealtimeActivity(serverIp);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    window.location.href = "/login";
  };

  const refreshData = () => {
    loadServers();
    if (selectedServer) {
      loadServerDetails(selectedServer);
      loadRealtimeActivity(selectedServer);
    }
  };

  if (!user) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="dashboard">
      {/* Header */}
      <div className="header">
        <div className="header-content">
          <div>
            <h1>Admin Dashboard</h1>
            <p>Welcome, {user.name}</p>
            <p>{user.email} | ID: {user.registered_id}</p>
          </div>
          <div className="header-buttons">
            <button onClick={refreshData} className="refresh-btn">Refresh Data</button>
            <button onClick={handleLogout} className="logout-btn">Logout</button>
          </div>
        </div>
      </div>

      <div className="main">
        <div className="servers-list">
          <h2>Servers (Last 7 Days)</h2>
          {servers.length === 0 ? (
            <p className="empty">No server activity in the last 7 days</p>
          ) : (
            servers.map((server) => (
              <div
                key={server.server_ip}
                className={`server-card ${selectedServer === server.server_ip ? "active" : ""}`}
                onClick={() => handleServerClick(server.server_ip)}
              >
                <h3>{server.server_name}</h3>
                <p>{server.server_ip}</p>
                <p>Sessions: {server.total_sessions}</p>
                <p>Users: {server.unique_users_count}</p>
                <p>Active: {server.active_sessions}</p>
              </div>
            ))
          )}
        </div>

        <div className="server-details">
          {selectedServer && serverDetails ? (
            <div>
              <h2>Server Details: {serverDetails.server_ip}</h2>
              <p>Total Sessions: {serverDetails.total_sessions}</p>
              <p>Total Commands: {serverDetails.total_commands}</p>
              <p>Active Now: {serverDetails.active_sessions.length}</p>

              {/* Tabs */}
              <div className="tabs">
                <button onClick={() => setActiveTab("sessions")} className={activeTab === "sessions" ? "active" : ""}>User Sessions</button>
                <button onClick={() => setActiveTab("commands")} className={activeTab === "commands" ? "active" : ""}>Commands</button>
                <button onClick={() => setActiveTab("realtime")} className={activeTab === "realtime" ? "active" : ""}>Realtime</button>
              </div>

              <div className="tab-content">
                {activeTab === "sessions" && (
                  serverDetails.sessions.length === 0 ? (
                    <p className="empty">No sessions</p>
                  ) : (
                    serverDetails.sessions.map((session) => (
                      <div key={session._id} className="session-card">
                        <h4>{session.user_id.name}</h4>
                        <p>Email: {session.user_id.email}</p>
                        <p>ID: {session.user_id.registered_id}</p>
                        <p>Status: {session.status}</p>
                        <p>Login: {formatTime(session.login_time)}</p>
                        <p>Logout: {session.logout_time ? formatTime(session.logout_time) : "Still active"}</p>
                        <p>Duration: {session.session_duration > 0 ? formatDuration(session.session_duration) : "Ongoing"}</p>
                        <p>SSH User: {session.ssh_username}</p>
                        <p>Commands: {session.commands_executed.length}</p>
                      </div>
                    ))
                  )
                )}

                {activeTab === "commands" && (
                  serverDetails.commands.length === 0 ? (
                    <p className="empty">No commands executed</p>
                  ) : (
                    serverDetails.commands.map((command) => (
                      <div key={command._id} className="command-card">
                        <h4>{command.user_id.name} ({command.user_id.registered_id})</h4>
                        <p>{formatTime(command.executed_at)}</p>
                        <p><strong>$</strong> {command.command}</p>
                        {command.output && <pre>{command.output}</pre>}
                        <p>Status: {command.status}</p>
                      </div>
                    ))
                  )
                )}

                {activeTab === "realtime" && (
                  realtimeActivity.length === 0 ? (
                    <p className="empty">No recent activity</p>
                  ) : (
                    realtimeActivity.map((activity) => (
                      <div key={activity._id} className="activity-card">
                        <h4>{activity.user_id.name} ({activity.user_id.registered_id})</h4>
                        <p>{formatTime(activity.executed_at)}</p>
                        <p><strong>$</strong> {activity.command}</p>
                      </div>
                    ))
                  )
                )}
              </div>
            </div>
          ) : (
            <p className="empty">Select a server to view details</p>
          )}
        </div>
      </div>

      {loading && <div className="loading-overlay">Loading server details...</div>}
    </div>
  );
};

export default AdminDashboard;
