import React, { useEffect, useRef } from "react";
import { io } from "socket.io-client";
import TerminalComponent from "../components/TerminalComponent";

export default function StudentDashboard() {
  const termRef = useRef(); // will hold Terminal instance
  const socketRef = useRef();

  useEffect(() => {
    if (!termRef.current) return;

    const socket = io("http://localhost:5000");
    socketRef.current = socket;

    socket.on("ssh-output", (data) => termRef.current.write(data));
    termRef.current.onData((data) => socket.emit("ssh-input", data));

    // connect with credentials (later you can replace with inputs)
    socket.emit("ssh-connect", {
      host: "192.168.1.100",
      username: "test",
      password: "1234",
    });

    return () => {
      socket.disconnect();
      termRef.current.dispose();
    };
  }, []);

  return (
    <div>
      <h1>Student Dashboard</h1>
      <TerminalComponent ref={termRef} />
    </div>
  );
}
