import React, { useState, useEffect, useCallback } from 'react';
import { 
  Users, 
  Server, 
  Terminal, 
  Clock, 
  Activity, 
  Eye, 
  AlertTriangle,
  RefreshCw,
  Search,
  Filter,
  BarChart3,
  User,
  ArrowLeft,
  Monitor,
  Command as CommandIcon,
  Calendar,
  Globe,
  Shield,
  Zap,
  Plus,
  Trash2,
  Edit,
  Save,
  X,
  Settings,
  Database,
  CheckCircle,
  XCircle,
  Loader
} from 'lucide-react';

const AdminDashboard = () => {
  const [dashboardStats, setDashboardStats] = useState(null);
  const [servers, setServers] = useState([]);
  const [students, setStudents] = useState([]);
  const [selectedServer, setSelectedServer] = useState(null);
  const [serverSessions, setServerSessions] = useState([]);
  const [selectedSession, setSelectedSession] = useState(null);
  const [sessionDetails, setSessionDetails] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [currentView, setCurrentView] = useState('overview');
  const [searchTerm, setSearchTerm] = useState('');

  // Server management states
  const [showAddServerModal, setShowAddServerModal] = useState(false);
  const [editingServer, setEditingServer] = useState(null);
  const [serverForm, setServerForm] = useState({
    server_name: '',
    server_ip: '',
    ssh_port: 22,
    ssh_username: '',
    description: '',
    is_active: true
  });
  const [serverActionLoading, setServerActionLoading] = useState(false);
  const [deleteConfirm, setDeleteConfirm] = useState(null);

  const API_BASE = 'http://localhost:5000/api';
  
  const getAuthHeaders = () => ({
    'Authorization': `Bearer ${localStorage.getItem('token')}`,
    'Content-Type': 'application/json'
  });

  // Fetch dashboard statistics
  const fetchDashboardStats = useCallback(async () => {
    try {
      const response = await fetch(`${API_BASE}/admin/dashboard/stats`, {
        headers: getAuthHeaders()
      });
      if (response.ok) {
        const data = await response.json();
        setDashboardStats(data);
      }
    } catch (err) {
      console.error('Error fetching dashboard stats:', err);
    }
  }, []);

  // Fetch servers list
  const fetchServers = useCallback(async () => {
    try {
      const response = await fetch(`${API_BASE}/admin/servers`, {
        headers: getAuthHeaders()
      });
      if (response.ok) {
        const data = await response.json();
        setServers(data);
      }
    } catch (err) {
      console.error('Error fetching servers:', err);
    }
  }, []);

  // Fetch students list
  const fetchStudents = useCallback(async () => {
    try {
      const response = await fetch(`${API_BASE}/admin/students`, {
        headers: getAuthHeaders()
      });
      if (response.ok) {
        const data = await response.json();
        setStudents(data);
      }
    } catch (err) {
      console.error('Error fetching students:', err);
    }
  }, []);

  // Add new server
  const addServer = useCallback(async (serverData) => {
    setServerActionLoading(true);
    try {
      const response = await fetch(`${API_BASE}/admin/servers`, {
        method: 'POST',
        headers: getAuthHeaders(),
        body: JSON.stringify(serverData)
      });

      if (response.ok) {
        const result = await response.json();
        setError('');
        fetchServers(); // Refresh servers list
        setShowAddServerModal(false);
        resetServerForm();
        // Show success message (you can implement a toast notification here)
        setError(`✅ Server "${serverData.server_name}" added successfully!`);
        setTimeout(() => setError(''), 3000);
      } else {
        const errorData = await response.json();
        setError(`Failed to add server: ${errorData.message || 'Unknown error'}`);
      }
    } catch (err) {
      console.error('Error adding server:', err);
      setError('Error adding server. Please try again.');
    } finally {
      setServerActionLoading(false);
    }
  }, [fetchServers]);

  // Update server
  const updateServer = useCallback(async (serverId, serverData) => {
    setServerActionLoading(true);
    try {
      const response = await fetch(`${API_BASE}/admin/servers/${serverId}`, {
        method: 'PUT',
        headers: getAuthHeaders(),
        body: JSON.stringify(serverData)
      });

      if (response.ok) {
        const result = await response.json();
        setError('');
        fetchServers(); // Refresh servers list
        setEditingServer(null);
        resetServerForm();
        setError(`✅ Server "${serverData.server_name}" updated successfully!`);
        setTimeout(() => setError(''), 3000);
      } else {
        const errorData = await response.json();
        setError(`Failed to update server: ${errorData.message || 'Unknown error'}`);
      }
    } catch (err) {
      console.error('Error updating server:', err);
      setError('Error updating server. Please try again.');
    } finally {
      setServerActionLoading(false);
    }
  }, [fetchServers]);

  // Delete server
  const deleteServer = useCallback(async (serverId) => {
    setServerActionLoading(true);
    try {
      const response = await fetch(`${API_BASE}/admin/servers/${serverId}`, {
        method: 'DELETE',
        headers: getAuthHeaders()
      });

      if (response.ok) {
        const result = await response.json();
        setError('');
        fetchServers(); // Refresh servers list
        setDeleteConfirm(null);
        setError(`✅ Server deleted successfully!`);
        setTimeout(() => setError(''), 3000);
      } else {
        const errorData = await response.json();
        setError(`Failed to delete server: ${errorData.message || 'Unknown error'}`);
      }
    } catch (err) {
      console.error('Error deleting server:', err);
      setError('Error deleting server. Please try again.');
    } finally {
      setServerActionLoading(false);
    }
  }, [fetchServers]);

  // Reset server form
  const resetServerForm = () => {
    setServerForm({
      server_name: '',
      server_ip: '',
      ssh_port: 22,
      ssh_username: '',
      description: '',
      is_active: true
    });
  };

  // Handle server form submission
  const handleServerFormSubmit = (e) => {
    e.preventDefault();
    
    // Basic validation
    if (!serverForm.server_name.trim() || !serverForm.server_ip.trim() || !serverForm.ssh_username.trim()) {
      setError('Please fill in all required fields (Name, IP, SSH Username)');
      return;
    }

    // Validate IP address format
    const ipRegex = /^(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$/;
    if (!ipRegex.test(serverForm.server_ip)) {
      setError('Please enter a valid IP address format (e.g., 192.168.1.100)');
      return;
    }

    // Validate port
    const port = parseInt(serverForm.ssh_port);
    if (port < 1 || port > 65535) {
      setError('SSH port must be between 1 and 65535');
      return;
    }

    if (editingServer) {
      updateServer(editingServer.id || editingServer._id, serverForm);
    } else {
      addServer(serverForm);
    }
  };

  // Handle edit server
  const handleEditServer = (server) => {
    setServerForm({
      server_name: server.server_name || '',
      server_ip: server.server_ip || '',
      ssh_port: server.ssh_port || 22,
      ssh_username: server.ssh_username || '',
      description: server.description || '',
      is_active: server.is_active !== undefined ? server.is_active : true
    });
    setEditingServer(server);
    setShowAddServerModal(true);
  };

  // Handle delete confirmation
  const handleDeleteConfirm = (server) => {
    setDeleteConfirm(server);
  };

  // Fetch server sessions
  const fetchServerSessions = useCallback(async (serverIp) => {
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE}/admin/servers/${serverIp}/sessions?limit=10`, {
        headers: getAuthHeaders()
      });
      if (response.ok) {
        const data = await response.json();
        setServerSessions(data.sessions);
        setSelectedServer(serverIp);
        setCurrentView('servers');
      }
    } catch (err) {
      setError('Error fetching server sessions');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  // Fetch session details
  const fetchSessionDetails = useCallback(async (sessionId) => {
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE}/admin/sessions/${sessionId}`, {
        headers: getAuthHeaders()
      });
      if (response.ok) {
        const data = await response.json();
        setSessionDetails(data);
        setSelectedSession(sessionId);
        setCurrentView('session-details');
      }
    } catch (err) {
      setError('Error fetching session details');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  // Initial data load
  useEffect(() => {
    fetchDashboardStats();
    fetchServers();
    fetchStudents();
  }, [fetchDashboardStats, fetchServers, fetchStudents]);

  // Auto-refresh every 30 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      fetchDashboardStats();
      fetchServers();
    }, 30000);
    return () => clearInterval(interval);
  }, [fetchDashboardStats, fetchServers]);

  const handleRefresh = () => {
    fetchDashboardStats();
    fetchServers();
    fetchStudents();
  };

  const formatDuration = (seconds) => {
    if (!seconds) return '0s';
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    
    if (hours > 0) return `${hours}h ${minutes}m`;
    if (minutes > 0) return `${minutes}m ${secs}s`;
    return `${secs}s`;
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleString();
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return '#10b981';
      case 'disconnected': return '#6b7280';
      case 'timeout': return '#f59e0b';
      default: return '#6b7280';
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const filteredServers = servers.filter(server => 
    server.server_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    server.server_ip?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const filteredStudents = students.filter(student =>
    student.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.registered_id?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // CSS Styles (keeping existing styles and adding new ones)
  const styles = `
    .admin-dashboard {
      min-height: 100vh;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    }

    .dashboard-header {
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(10px);
      border-bottom: 1px solid rgba(255, 255, 255, 0.2);
      padding: 1rem 2rem;
      display: flex;
      justify-content: space-between;
      align-items: center;
      box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    }

    .dashboard-title {
      color: white;
      margin: 0;
      font-size: 1.8rem;
      font-weight: 600;
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }

    .dashboard-controls {
      display: flex;
      align-items: center;
      gap: 1rem;
    }

    .btn {
      padding: 0.5rem 1rem;
      border: none;
      border-radius: 8px;
      cursor: pointer;
      font-weight: 500;
      transition: all 0.2s ease;
      display: flex;
      align-items: center;
      gap: 0.5rem;
      text-decoration: none;
    }

    .btn-primary {
      background: rgba(59, 130, 246, 0.8);
      color: white;
    }

    .btn-primary:hover {
      background: rgba(18, 86, 196, 1);
      transform: translateY(-1px);
    }

    .btn-secondary {
      background: rgba(255, 255, 255, 0.1);
      color: white;
      border: 1px solid rgba(255, 255, 255, 0.2);
    }

    .btn-secondary:hover {
      background: rgba(255, 255, 255, 0.2);
    }

    .btn-success {
      background: rgba(34, 197, 94, 0.8);
      color: white;
    }

    .btn-success:hover {
      background: rgba(34, 197, 94, 1);
    }

    .btn-warning {
      background: rgba(245, 158, 11, 0.8);
      color: white;
    }

    .btn-warning:hover {
      background: rgba(245, 158, 11, 1);
    }

    .btn-danger {
      background: rgba(239, 68, 68, 0.8);
      color: white;
    }

    .btn-danger:hover {
      background: rgba(239, 68, 68, 1);
    }

    .btn-small {
      padding: 0.25rem 0.75rem;
      font-size: 0.875rem;
    }

    .dashboard-content {
      padding: 2rem;
      max-width: 1400px;
      margin: 0 auto;
    }

    .navigation-tabs {
      display: flex;
      gap: 0.5rem;
      margin-bottom: 2rem;
      background: rgba(255, 255, 255, 0.1);
      border-radius: 12px;
      padding: 0.5rem;
    }

    .nav-tab {
      padding: 0.75rem 1.5rem;
      border-radius: 8px;
      border: none;
      background: transparent;
      color: rgba(255, 255, 255, 1);
      cursor: pointer;
      transition: all 0.2s ease;
      display: flex;
      align-items: center;
      gap: 0.5rem;
      font-weight: 500;
    }

    .nav-tab.active {
      background: rgba(255, 255, 255, 0.2);
      color: white;
      backdrop-filter: blur(10px);
    }

    .nav-tab:hover:not(.active) {
      background: rgba(255, 255, 255, 0.1);
      color: rgba(255, 255, 255, 0.9);
    }

    .stats-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
      gap: 1.5rem;
      margin-bottom: 2rem;
    }

    .stat-card {
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(10px);
      border: 1px solid rgba(255, 255, 255, 0.2);
      border-radius: 16px;
      padding: 1.5rem;
      color: white;
      transition: transform 0.2s ease, box-shadow 0.2s ease;
    }

    .stat-card:hover {
      transform: translateY(-4px);
      box-shadow: 0 8px 25px rgba(0, 0, 0, 0.2);
    }

    .stat-header {
      display: flex;
      align-items: center;
      justify-content: space-between;
      margin-bottom: 1rem;
    }

    .stat-icon {
      padding: 0.75rem;
      border-radius: 12px;
      background: rgba(255, 255, 255, 0.1);
    }

    .stat-value {
      font-size: 2rem;
      font-weight: 700;
      margin-bottom: 0.25rem;
    }

    .stat-label {
      color: rgba(255, 255, 255, 0.8);
      font-size: 0.9rem;
    }

    .search-bar {
      display: flex;
      align-items: center;
      gap: 1rem;
      margin-bottom: 2rem;
    }

    .search-input {
      flex: 1;
      padding: 0.75rem 1rem;
      border: 1px solid rgba(255, 255, 255, 0.2);
      border-radius: 12px;
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(10px);
      color: white;
      font-size: 1rem;
    }

    .search-input::placeholder {
      color: rgba(255, 255, 255, 0.6);
    }

    .search-input:focus {
      outline: none;
      border-color: rgba(59, 130, 246, 0.5);
      box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
    }

    .content-grid {
      display: grid;
      gap: 1.5rem;
    }

    .servers-grid {
      display: grid;
      grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
      gap: 1.5rem;
    }

    .server-card {
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(10px);
      border: 1px solid rgba(255, 255, 255, 0.2);
      border-radius: 16px;
      padding: 1.5rem;
      color: white;
      cursor: pointer;
      transition: all 0.2s ease;
      position: relative;
    }

    .server-card:hover {
      transform: translateY(-4px);
      box-shadow: 0 8px 25px rgba(0, 0, 0, 0.2);
      background: rgba(255, 255, 255, 0.15);
    }

    .server-card-actions {
      position: absolute;
      top: 1rem;
      right: 1rem;
      display: flex;
      gap: 0.5rem;
      opacity: 0;
      transition: opacity 0.2s ease;
    }

    .server-card:hover .server-card-actions {
      opacity: 1;
    }

    .server-header {
      display: flex;
      align-items: center;
      gap: 1rem;
      margin-bottom: 1rem;
    }

    .server-status {
      display: flex;
      align-items: center;
      gap: 0.5rem;
      font-size: 0.875rem;
    }

    .status-dot {
      width: 8px;
      height: 8px;
      border-radius: 50%;
      background: #10b981;
    }

    .server-stats {
      display: grid;
      grid-template-columns: repeat(2, 1fr);
      gap: 1rem;
      margin-top: 1rem;
    }

    .server-stat {
      text-align: center;
    }

    .server-stat-value {
      font-size: 1.25rem;
      font-weight: 600;
      color: #ffffffff;
    }

    .server-stat-label {
      font-size: 0.75rem;
      color: rgba(255, 255, 255, 0.7);
    }

    .session-table {
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(10px);
      border: 1px solid rgba(255, 255, 255, 0.2);
      border-radius: 16px;
      overflow: hidden;
    }

    .table-header {
      background: rgba(255, 255, 255, 0.1);
      padding: 1rem;
      border-bottom: 1px solid rgba(255, 255, 255, 0.2);
      display: flex;
      align-items: center;
      justify-content: space-between;
    }

    .table {
      width: 100%;
      color: white;
    }

    .table thead {
      background: rgba(255, 255, 255, 0.05);
    }

    .table th,
    .table td {
      padding: 1rem;
      text-align: left;
      border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    }

    .table th {
      font-weight: 600;
      color: rgba(255, 255, 255, 0.9);
      font-size: 0.875rem;
      text-transform: uppercase;
      letter-spacing: 0.05em;
    }

    .table tbody tr {
      cursor: pointer;
      transition: background-color 0.2s ease;
    }

    .table tbody tr:hover {
      background: rgba(255, 255, 255, 0.05);
    }

    .status-badge {
      display: inline-flex;
      align-items: center;
      gap: 0.25rem;
      padding: 0.25rem 0.75rem;
      border-radius: 20px;
      font-size: 0.75rem;
      font-weight: 500;
    }

    .status-active {
      background: rgba(16, 185, 129, 0.2);
      color: #10b981;
      border: 1px solid rgba(16, 185, 129, 0.3);
    }

    .status-inactive {
      background: rgba(239, 68, 68, 0.2);
      color: #f87171;
      border: 1px solid rgba(239, 68, 68, 0.3);
    }

    .status-disconnected {
      background: rgba(107, 114, 128, 0.2);
      color: #9ca3af;
      border: 1px solid rgba(107, 114, 128, 0.3);
    }

    .session-details {
      display: grid;
      gap: 1.5rem;
    }

    .session-info-card {
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(10px);
      border: 1px solid rgba(255, 255, 255, 0.2);
      border-radius: 16px;
      padding: 1.5rem;
      color: white;
    }

    .session-info-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
      gap: 1rem;
    }

    .info-item {
      display: flex;
      flex-direction: column;
      gap: 0.25rem;
    }

    .info-label {
      font-size: 0.75rem;
      color: rgba(255, 255, 255, 0.7);
      text-transform: uppercase;
      letter-spacing: 0.05em;
    }

    .info-value {
      font-size: 1rem;
      font-weight: 500;
    }

    .commands-section {
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(10px);
      border: 1px solid rgba(255, 255, 255, 0.2);
      border-radius: 16px;
      padding: 1.5rem;
      color: white;
    }

    .command-item {
      background: rgba(0, 0, 0, 0.2);
      border-radius: 8px;
      padding: 1rem;
      margin-bottom: 1rem;
      border-left: 4px solid #60a5fa;
    }

    .command-header {
      display: flex;
      justify-content: between;
      align-items: center;
      margin-bottom: 0.5rem;
    }

    .command-text {
      font-family: 'Courier New', monospace;
      font-weight: 600;
      color: #60a5fa;
      flex: 1;
    }

    .command-time {
      font-size: 0.75rem;
      color: rgba(255, 255, 255, 0.6);
    }

    .command-output {
      background: rgba(0, 0, 0, 0.3);
      border-radius: 4px;
      padding: 0.75rem;
      margin-top: 0.5rem;
      font-family: 'Courier New', monospace;
      font-size: 0.875rem;
      white-space: pre-wrap;
      max-height: 200px;
      overflow-y: auto;
      border: 1px solid rgba(255, 255, 255, 0.1);
    }

    .loading-spinner {
      display: flex;
      justify-content: center;
      align-items: center;
      padding: 2rem;
      color: white;
    }

    .error-message {
      background: rgba(239, 68, 68, 0.1);
      border: 1px solid rgba(239, 68, 68, 0.3);
      color: #f87171;
      padding: 1rem;
      border-radius: 12px;
      margin-bottom: 1rem;
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }

    .success-message {
      background: rgba(34, 197, 94, 0.1);
      border: 1px solid rgba(34, 197, 94, 0.3);
      color: #4ade80;
      padding: 1rem;
      border-radius: 12px;
      margin-bottom: 1rem;
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }

    .breadcrumb {
      display: flex;
      align-items: center;
      gap: 0.5rem;
      margin-bottom: 1rem;
      color: rgba(255, 255, 255, 0.8);
    }

    .breadcrumb-item {
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }

    .breadcrumb-separator {
      color: rgba(255, 255, 255, 0.5);
    }

    /* Modal Styles */
    .modal-overlay {
      position: fixed;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background: rgba(0, 0, 0, 0.5);
      display: flex;
      align-items: center;
      justify-content: center;
      z-index: 1000;
      backdrop-filter: blur(4px);
    }

    .modal-content {
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(20px);
      border: 1px solid rgba(255, 255, 255, 0.2);
      border-radius: 16px;
      padding: 2rem;
      max-width: 500px;
      width: 90%;
      color: white;
      max-height: 90vh;
      overflow-y: auto;
    }

    .modal-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 1.5rem;
    }

    .modal-title {
      margin: 0;
      font-size: 1.25rem;
      font-weight: 600;
    }

    .form-group {
      margin-bottom: 1rem;
    }

    .form-label {
      display: block;
      margin-bottom: 0.5rem;
      font-weight: 500;
      color: rgba(255, 255, 255, 0.9);
    }

    .form-input {
      width: 100%;
      padding: 0.75rem;
      border: 1px solid rgba(255, 255, 255, 0.2);
      border-radius: 8px;
      background: rgba(255, 255, 255, 0.1);
      color: white;
      font-size: 1rem;
      box-sizing: border-box;
    }

    .form-input::placeholder {
      color: rgba(255, 255, 255, 0.5);
    }

    .form-input:focus {
      outline: none;
      border-color: rgba(59, 130, 246, 0.5);
      box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
    }

    .form-textarea {
      min-height: 100px;
      resize: vertical;
    }

    .form-checkbox {
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }

    .form-checkbox input {
      width: auto;
    }

    .modal-actions {
      display: flex;
      gap: 1rem;
      justify-content: flex-end;
      margin-top: 1.5rem;
    }

    .confirm-dialog {
      background: rgba(239, 68, 68, 0.1);
      border: 1px solid rgba(239, 68, 68, 0.3);
      border-radius: 12px;
      padding: 1.5rem;
      text-align: center;
    }

    .confirm-dialog h3 {
      margin-top: 0;
      color: #f87171;
    }

    .confirm-dialog p {
      margin-bottom: 1.5rem;
      color: rgba(255, 255, 255, 0.8);
    }

    .server-management-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 1.5rem;
    }

    .servers-management-grid {
      display: grid;
      gap: 1.5rem;
    }

    @media (max-width: 768px) {
      .dashboard-header {
        flex-direction: column;
        gap: 1rem;
        text-align: center;
      }

      .dashboard-controls {
        flex-wrap: wrap;
        justify-content: center;
      }

      .stats-grid {
        grid-template-columns: 1fr;
      }

      .servers-grid {
        grid-template-columns: 1fr;
      }

      .navigation-tabs {
        flex-wrap: wrap;
      }

      .search-bar {
        flex-direction: column;
        align-items: stretch;
      }

      .modal-content {
        width: 95%;
        padding: 1.5rem;
      }

      .modal-actions {
        flex-direction: column;
      }
    }

    .animate-pulse {
      animation: pulse 2s infinite;
    }

    @keyframes pulse {
      0%, 100% { opacity: 1; }
      50% { opacity: 0.5; }
    }
  `};
  export default AdminDashboard;
