import React, { useEffect, useState } from "react";
import axios from "axios";

export default function AdminDashboard() {
  const [logs, setLogs] = useState([]);

  useEffect(() => {
    const fetchLogs = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/admin/logs");
        setLogs(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchLogs();
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Admin Dashboard</h2>
      <table className="border-collapse border border-gray-400 w-full">
        <thead>
          <tr className="bg-gray-200">
            <th className="border border-gray-400 p-2">Student</th>
            <th className="border border-gray-400 p-2">Email</th>
            <th className="border border-gray-400 p-2">Server IP</th>
            <th className="border border-gray-400 p-2">Time Spent</th>
            <th className="border border-gray-400 p-2">Commands</th>
          </tr>
        </thead>
        <tbody>
          {logs.map((log) => {
            const timeSpent = log.disconnectedAt
              ? ((new Date(log.disconnectedAt) - new Date(log.connectedAt)) / 1000).toFixed(0) + "s"
              : "Active";

            return (
              <tr key={log._id}>
                <td className="border p-2">{log.studentId?.name || "Unknown"}</td>
                <td className="border p-2">{log.studentEmail}</td>
                <td className="border p-2">{log.serverIp}</td>
                <td className="border p-2">{timeSpent}</td>
                <td className="border p-2 whitespace-pre-wrap">
                  {log.commands?.join("\n")}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
