// TerminalPageComplete.js - Full Terminal Page with Database Integration

import React, { useState, useEffect, useRef, useCallback } from "react";
import { io } from "socket.io-client";
import { Terminal } from "xterm";
import "xterm/css/xterm.css";
import { FitAddon } from 'xterm-addon-fit';
import serverDataService from './serverDataService';
import './TerminalPage.css';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || "http://localhost:5000";

// Global singleton pattern - prevent multiple instances
let globalTerminalInstance = null;
let globalSocketInstance = null;
let globalIsInitialized = false;
let globalFitAddon = null;
let globalInputHandler = null;
let globalKeyHandler = null;
let globalEventListenersAttached = false;

const TerminalPage = () => {
  // State management
  const [isConnected, setIsConnected] = useState(false);
  const [connectionData, setConnectionData] = useState(null);
  const [serverData, setServerData] = useState(null);
  const [error, setError] = useState("");
  const [connectionStatus, setConnectionStatus] = useState("initializing");
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [terminalTheme, setTerminalTheme] = useState("dark");
  const [showServerPanel, setShowServerPanel] = useState(false);
  const [connectionStats, setConnectionStats] = useState({
    startTime: null,
    dataReceived: 0,
    dataSent: 0,
    commandCount: 0
  });
  
  // Refs that don't cause re-renders
  const terminalRef = useRef();
  const terminalInstance = useRef();
  const socketRef = useRef();
  const isInitializedRef = useRef(false);
  const connectionTimeoutRef = useRef(null);
  const statsIntervalRef = useRef(null);

  // Get stored user data from localStorage
  const getStoredUserData = useCallback(() => {
    try {
      const userData = localStorage.getItem('user');
      const token = localStorage.getItem('token');
      
      if (userData) {
        const user = JSON.parse(userData);
        return {
          token: token || '',
          userId: user.id || user._id || '',
          userEmail: user.email || '',
          userName: user.name || '',
          registeredId: user.registered_id || '',
          userRole: user.role || 'student'
        };
      }
    } catch (err) {
      console.warn("Could not retrieve stored user data:", err);
    }
    return {};
  }, []);

  // Fetch complete server data from database
  const fetchCompleteServerData = useCallback(async (serverId, ipAddress) => {
    if (!serverId && !ipAddress) return null;
    
    try {
      setError(""); // Clear any previous errors
      
      let server = null;
      
      if (serverId) {
        server = await serverDataService.getServerById(serverId);
      } else if (ipAddress) {
        server = await serverDataService.getServerByIp(ipAddress);
      }
      
      if (server) {
        console.log("âœ… Complete server data fetched:", server);
        setServerData(server);
        
        // Check if server is available for connections
        const isAvailable = await serverDataService.isServerAvailable(server._id || server.id);
        if (!isAvailable) {
          setError("Server is not available for new connections (maintenance mode or maximum connections reached)");
        }
        
        return server;
      }
    } catch (error) {
      console.warn("Could not fetch complete server data:", error);
      setError(`Failed to fetch server details: ${error.message}`);
    }
    return null;
  }, []);

  // Enhanced SSH connection with database logging
  const connectToSSHServer = useCallback(async (socketInstance, data, term) => {
    if (!socketInstance || !data || !term) {
      console.error("Missing socket instance, connection data, or terminal");
      if (term) term.write("\u001b[31mâŒ Cannot connect: Missing connection data\u001b[0m\r\n");
      return;
    }

    console.log("ðŸ”— Attempting SSH connection with enhanced data:", data);
    
    // Log connection attempt
    if (data.serverId) {
      await serverDataService.logServerConnection(data.serverId, {
        userId: data.userId,
        userName: data.userName,
        userEmail: data.userEmail,
        action: 'connection_attempt',
        host: data.host,
        username: data.username,
        sessionId: data.sessionId
      });
    }

    const sshConnectionData = {
      host: data.host,
      username: data.username,
      password: data.password,
      port: data.port || 22,
      
      user: {
        id: data.userId,
        name: data.userName,
        email: data.userEmail,
        registered_id: data.registeredId,
        role: data.userRole
      },
      
      session: {
        id: data.sessionId,
        token: data.token,
        connectionTime: data.connectionTime,
        serverName: data.serverName,
        dashboardUrl: data.dashboardUrl,
        serverId: data.serverId
      },
      
      server: serverData ? {
        id: serverData._id || serverData.id,
        name: serverData.name,
        ip: serverData.ip,
        port: serverData.port,
        os: serverData.os,
        location: serverData.location,
        max_connections: serverData.max_connections
      } : null,
      
      browser: {
        userAgent: data.browserInfo || navigator.userAgent,
        windowId: window.name,
        referrer: document.referrer,
        viewport: {
          width: window.innerWidth,
          height: window.innerHeight
        }
      }
    };

    term.write("\u001b[36mðŸ”— Connecting to SSH server...\u001b[0m\r\n");
    term.write(`\u001b[33mðŸ“¡ Host:\u001b[0m ${data.host}:${data.port || 22}\r\n`);
    term.write(`\u001b[33mðŸ‘¤ User:\u001b[0m ${data.username}\r\n`);
    if (serverData) {
      term.write(`\u001b[33mðŸ–¥ï¸ Server:\u001b[0m ${serverData.name}\r\n`);
      term.write(`\u001b[33mðŸŒ Location:\u001b[0m ${serverData.location || 'Unknown'}\r\n`);
      term.write(`\u001b[33mðŸ’¾ OS:\u001b[0m ${serverData.os || 'Linux'}\r\n`);
    }
    term.write("\u001b[35mðŸ” Authenticating...\u001b[0m\r\n\r\n");

    // Send enhanced connection request
    socketInstance.emit("ssh-connect", sshConnectionData);
    
    // Enhanced user activity tracking
    socketInstance.emit("user-activity", {
      type: 'terminal_access',
      userId: data.userId,
      userName: data.userName,
      userEmail: data.userEmail,
      action: 'ssh_connect_attempt',
      target: data.host,
      timestamp: new Date().toISOString(),
      sessionId: data.sessionId,
      serverId: data.serverId,
      serverName: serverData?.name || data.serverName,
      browserInfo: navigator.userAgent,
      connectionDetails: {
        host: data.host,
        port: data.port || 22,
        username: data.username
      }
    });

    // Set connection timeout
    if (connectionTimeoutRef.current) {
      clearTimeout(connectionTimeoutRef.current);
    }

    connectionTimeoutRef.current = setTimeout(() => {
      term.write("\u001b[33mâ±ï¸ Connection is taking longer than expected...\u001b[0m\r\n");
      term.write("\u001b[36mðŸ’¡ Check if your backend server is handling SSH connections properly\u001b[0m\r\n");
      if (serverData?.max_connections) {
        term.write(`\u001b[36mðŸ“Š Server max connections: ${serverData.max_connections}\u001b[0m\r\n`);
      }
      term.write("\r\n");
    }, 10000);

    // Start connection stats tracking
    setConnectionStats(prev => ({
      ...prev,
      startTime: new Date()
    }));

  }, [serverData]);

  // Enhanced cleanup with database logging
  const cleanupConnection = useCallback(async () => {
    console.log("ðŸ§¹ GLOBAL CLEANUP: Enhanced cleanup starting...");
    
    // Log disconnection
    if (connectionData?.serverId) {
      try {
        await serverDataService.logServerConnection(connectionData.serverId, {
          userId: connectionData.userId,
          userName: connectionData.userName,
          action: 'connection_closed',
          sessionId: connectionData.sessionId,
          duration: connectionStats.startTime ? 
            Math.floor((new Date() - connectionStats.startTime) / 1000) : 0,
          stats: connectionStats
        });
      } catch (error) {
        console.warn("Failed to log disconnection:", error);
      }
    }
    
    // Clear timers
    if (connectionTimeoutRef.current) {
      clearTimeout(connectionTimeoutRef.current);
      connectionTimeoutRef.current = null;
    }
    
    if (statsIntervalRef.current) {
      clearInterval(statsIntervalRef.current);
      statsIntervalRef.current = null;
    }
    
    // Clean up global handlers
    if (globalInputHandler) {
      globalInputHandler.dispose();
      globalInputHandler = null;
    }
    if (globalKeyHandler) {
      globalKeyHandler.dispose();
      globalKeyHandler = null;
    }
    
    // Clean up socket
    if (globalSocketInstance) {
      globalSocketInstance.removeAllListeners();
      globalSocketInstance.disconnect();
      globalSocketInstance = null;
    }
    
    // Clean up terminal
    if (globalTerminalInstance && !globalTerminalInstance.isDisposed) {
      globalTerminalInstance.dispose();
      globalTerminalInstance = null;
    }
    
    // Reset global flags
    globalIsInitialized = false;
    globalFitAddon = null;
    globalEventListenersAttached = false;
    
    // Clean up session storage
    Object.keys(sessionStorage).forEach(key => {
      if (key.startsWith('terminal_')) {
        sessionStorage.removeItem(key);
      }
    });
    
    console.log("âœ… Enhanced cleanup complete");
  }, [connectionData, connectionStats]);

  // Enhanced socket event listeners with stats tracking
  const setupSocketEventListeners = useCallback((socket, term, data) => {
    if (globalEventListenersAttached) {
      console.log("ðŸ›‘ Socket event listeners already attached globally, skipping...");
      return;
    }

    console.log("ðŸŽ§ Setting up enhanced socket event listeners...");
    globalEventListenersAttached = true;

    let sshConnectedHandled = false;

    socket.on("connect", () => {
      console.log("âœ… GLOBAL SOCKET: Connected to terminal server");
      setIsConnected(true);
      setConnectionStatus("connected");
      term.write("\u001b[32mâœ… Socket connected to " + SERVER_URL + "\u001b[0m\r\n");
      term.write("\u001b[36mðŸ”— Establishing SSH connection...\u001b[0m\r\n\r\n");
      
      connectToSSHServer(socket, data, term);
    });

    socket.on("ssh-output", (outputData) => {
      if (term && !term.isDisposed) {
        term.write(outputData);
        
        // Track data received
        setConnectionStats(prev => ({
          ...prev,
          dataReceived: prev.dataReceived + outputData.length
        }));
      }
    });

    socket.on("ssh-connected", async (info) => {
      if (sshConnectedHandled) {
        console.log("âš ï¸ SSH connected event already handled globally, ignoring duplicate");
        return;
      }
      sshConnectedHandled = true;
      
      if (connectionTimeoutRef.current) {
        clearTimeout(connectionTimeoutRef.current);
        connectionTimeoutRef.current = null;
      }
      
      console.log("ðŸŽ‰ GLOBAL SSH: Connection established successfully");
      setConnectionStatus("ssh-connected");
      setError("");
      
      term.write("\r\n\u001b[32mðŸŽ‰ SSH CONNECTION ESTABLISHED! ðŸŽ‰\u001b[0m\r\n");
      term.write(`\u001b[32mâœ… Connected to:\u001b[0m ${info.host || data.host}\r\n`);
      term.write(`\u001b[33mðŸ‘¤ User:\u001b[0m ${data.userName || 'Unknown'}\r\n`);
      term.write(`\u001b[35mðŸ†” Student ID:\u001b[0m ${data.registeredId || 'No ID'}\r\n`);
      term.write(`\u001b[36mâ° Connected at:\u001b[0m ${new Date().toLocaleString()}\r\n`);
      
      if (serverData) {
        term.write(`\u001b[34mðŸ–¥ï¸ Server Name:\u001b[0m ${serverData.name}\r\n`);
        if (serverData.location) {
          term.write(`\u001b[34mðŸŒ Location:\u001b[0m ${serverData.location}\r\n`);
        }
        if (serverData.os) {
          term.write(`\u001b[34mðŸ’¾ OS:\u001b[0m ${serverData.os}\r\n`);
        }
      }
      
      term.write("=" .repeat(60) + "\r\n");
      term.write("\u001b[36mðŸ’¡ You can now run commands like: pwd, ls, whoami, top\u001b[0m\r\n");
      term.write("=" .repeat(60) + "\r\n\r\n");
      
      // Log successful connection
      if (data.serverId) {
        try {
          await serverDataService.logServerConnection(data.serverId, {
            userId: data.userId,
            userName: data.userName,
            action: 'connection_established',
            sessionId: data.sessionId,
            connectionInfo: info
          });
        } catch (error) {
          console.warn("Failed to log successful connection:", error);
        }
      }
      
      // Start stats interval
      if (statsIntervalRef.current) {
        clearInterval(statsIntervalRef.current);
      }
      
      statsIntervalRef.current = setInterval(() => {
        setConnectionStats(prev => ({
          ...prev,
          commandCount: prev.commandCount + 1
        }));
      }, 60000); // Update stats every minute
    });

    socket.on("ssh-error", async (errorData) => {
      const errorMsg = errorData.message || errorData;
      term.write(`\r\n\u001b[31mâŒ SSH Connection Error: ${errorMsg}\u001b[0m\r\n`);
      
      if (serverData && serverData.max_connections) {
        const connectionCount = await serverDataService.getServerConnectionCount(serverData._id || serverData.id);
        term.write(`\u001b[33mðŸ“Š Current connections: ${connectionCount}/${serverData.max_connections}\u001b[0m\r\n`);
      }
      
      term.write("\r\n\u001b[33mðŸ”„ Retrying connection in 3 seconds...\u001b[0m\r\n");
      setConnectionStatus("error");
      setError(errorMsg);
      
      // Log connection error
      if (data.serverId) {
        try {
          await serverDataService.logServerConnection(data.serverId, {
            userId: data.userId,
            userName: data.userName,
            action: 'connection_error',
            sessionId: data.sessionId,
            error: errorMsg
          });
        } catch (error) {
          console.warn("Failed to log connection error:", error);
        }
      }
      
      setTimeout(() => {
        if (globalSocketInstance && data) {
          connectToSSHServer(globalSocketInstance, data, term);
        }
      }, 3000);
    });

    socket.on("ssh-disconnected", async () => {
      term.write("\r\n\u001b[33mðŸ”Œ SSH connection closed\u001b[0m\r\n");
      setConnectionStatus("disconnected");
      setIsConnected(false);
      
      // Stop stats interval
      if (statsIntervalRef.current) {
        clearInterval(statsIntervalRef.current);
        statsIntervalRef.current = null;
      }
      
      // Log disconnection
      if (data.serverId) {
        try {
          await serverDataService.logServerConnection(data.serverId, {
            userId: data.userId,
            userName: data.userName,
            action: 'connection_disconnected',
            sessionId: data.sessionId,
            duration: connectionStats.startTime ? 
              Math.floor((new Date() - connectionStats.startTime) / 1000) : 0
          });
        } catch (error) {
          console.warn("Failed to log disconnection:", error);
        }
      }
    });

    socket.on("disconnect", (reason) => {
      setIsConnected(false);
      setConnectionStatus("disconnected");
      term.write(`\r\n\u001b[31m[Socket disconnected: ${reason}]\u001b[0m\r\n`);
      
      if (reason !== 'io client disconnect') {
        setTimeout(() => {
          if (globalSocketInstance && !globalSocketInstance.connected) {
            term.write("\r\n\u001b[36mðŸ”„ Attempting to reconnect...\u001b[0m\r\n");
            globalSocketInstance.connect();
          }
        }, 2000);
      }
    });

    socket.on("connect_error", (err) => {
      const errorMsg = `Connection error: ${err.message}`;
      setError(errorMsg);
      setConnectionStatus("error");
      term.write(`\r\n\u001b[31mâŒ ${errorMsg}\u001b[0m\r\n`);
      term.write(`\r\n\u001b[36mðŸ’¡ Make sure your backend server is running on ${SERVER_URL}\u001b[0m\r\n`);
    });
  }, [connectToSSHServer, serverData, connectionStats]);

  // Enhanced input handlers with command tracking
  const setupTerminalInputHandlers = useCallback((term, socket) => {
    if (globalInputHandler) {
      globalInputHandler.dispose();
      globalInputHandler = null;
    }
    if (globalKeyHandler) {
      globalKeyHandler.dispose();
      globalKeyHandler = null;
    }

    globalInputHandler = term.onData((input) => {
      if (socket && socket.connected) {
        socket.emit("ssh-input", input);
        
        // Track data sent and commands
        setConnectionStats(prev => ({
          ...prev,
          dataSent: prev.dataSent + input.length,
          commandCount: input.includes('\r') ? prev.commandCount + 1 : prev.commandCount
        }));
      } else {
        term.write("\r\n\u001b[31mâŒ Not connected to server. Cannot send input.\u001b[0m\r\n");
      }
    });

    globalKeyHandler = term.onKey(({ key, domEvent }) => {
      if (domEvent.ctrlKey && domEvent.key === 'c') {
        if (socket && socket.connected) {
          socket.emit("ssh-input", '\x03');
        }
      }
    });
  }, []);

  // Get terminal theme configuration
  const getTerminalTheme = (themeName) => {
    const themes = {
      dark: {
        background: '#0d1117',
        foreground: '#f0f6fc',
        cursor: '#f0f6fc',
        selection: '#264f78',
        black: '#484f58',
        red: '#ff7b72',
        green: '#7ee787',
        yellow: '#ffa657',
        blue: '#79c0ff',
        magenta: '#d2a8ff',
        cyan: '#a5f3fc',
        white: '#f0f6fc'
      },
      light: {
        background: '#ffffff',
        foreground: '#24292f',
        cursor: '#24292f',
        selection: '#0969da',
        black: '#24292f',
        red: '#cf222e',
        green: '#116329',
        yellow: '#4d2d00',
        blue: '#0969da',
        magenta: '#8250df',
        cyan: '#1b7c83',
        white: '#6e7781'
      },
      matrix: {
        background: '#000000',
        foreground: '#00ff41',
        cursor: '#00ff41',
        selection: '#00ff4150',
        black: '#000000',
        red: '#ff0000',
        green: '#00ff41',
        yellow: '#ffff00',
        blue: '#0000ff',
        magenta: '#ff00ff',
        cyan: '#00ffff',
        white: '#ffffff'
      }
    };
    return themes[themeName] || themes.dark;
  };

  // Enhanced terminal initialization
  const initializeTerminal = useCallback((data) => {
    if (globalIsInitialized) {
      if (globalTerminalInstance && terminalRef.current && !globalTerminalInstance.isDisposed) {
        terminalInstance.current = globalTerminalInstance;
        socketRef.current = globalSocketInstance;
        setIsConnected(!!globalSocketInstance?.connected);
        setConnectionStatus(globalSocketInstance?.connected ? "ssh-connected" : "connected");
      }
      return;
    }
    
    globalIsInitialized = true;
    isInitializedRef.current = true;
    setConnectionStatus("connecting");
    
    if (!globalFitAddon) {
      globalFitAddon = new FitAddon();
    }
    
    const term = new Terminal({
      cursorBlink: true,
      theme: getTerminalTheme(terminalTheme),
      fontSize: 14,
      fontFamily: '"Cascadia Code", "Fira Code", "JetBrains Mono", Consolas, Monaco, "Courier New", monospace',
      fontWeight: 'normal',
      fontWeightBold: 'bold',
      lineHeight: 1.2,
      letterSpacing: 0,
      rows: 30,
      cols: 120,
      scrollback: 5000,
      convertEol: true,
      disableStdin: false,
      localEcho: false,
      allowProposedApi: true,
      bellStyle: 'sound'
    });

    term.loadAddon(globalFitAddon);

    if (terminalRef.current) {
      term.open(terminalRef.current);
      
      setTimeout(() => {
        if (globalFitAddon && !term.isDisposed) {
          try {
            globalFitAddon.fit();
          } catch (err) {
            console.warn("Initial fit error:", err);
          }
        }
      }, 100);
      
      globalTerminalInstance = term;
      terminalInstance.current = term;
      
      term.write('\u001b[32mðŸš€ TERMINAL INITIALIZED\u001b[0m\r\n');
      term.write(`\u001b[36mðŸ“¡ Connecting to ${SERVER_URL}...\u001b[0m\r\n`);
      if (serverData) {
        term.write(`\u001b[33mðŸ–¥ï¸ Target Server: ${serverData.name} (${serverData.ip})\u001b[0m\r\n`);
      }
      term.write('\r\n');
    }
    
    if (globalSocketInstance) {
      globalSocketInstance.removeAllListeners();
      globalSocketInstance.disconnect();
    }
    
    const newSocket = io(SERVER_URL, {
      query: {
        userId: data.userId || '',
        userName: data.userName || '',
        userRole: data.userRole || 'student',
        serverId: data.serverId || ''
      },
      timeout: 10000,
      forceNew: true,
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
      transports: ['websocket', 'polling']
    });
    
    globalSocketInstance = newSocket;
    socketRef.current = newSocket;

    setupSocketEventListeners(newSocket, term, data);
    setupTerminalInputHandlers(term, newSocket);

    const handleResize = () => {
      if (term && term.element && globalFitAddon && !term.isDisposed) {
        try {
          globalFitAddon.fit();
        } catch (fitError) {
          console.warn("Fit error:", fitError);
          if (terminalRef.current) {
            const cols = Math.floor(terminalRef.current.clientWidth / 9);
            const rows = Math.floor(terminalRef.current.clientHeight / 17);
            term.resize(cols, rows);
          }
        }
      }
    };
    
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      terminalInstance.current = null;
      socketRef.current = null;
    };
  }, [setupSocketEventListeners, setupTerminalInputHandlers, terminalTheme, serverData]);

  // Enhanced connection data parsing with validation
  const parseConnectionData = useCallback((rawData) => {
    console.log("ðŸ” Parsing connection data:", rawData);
    
    const storedUserData = getStoredUserData();
    const validation = serverDataService.validateConnectionData(rawData);
    
    if (!validation.isValid) {
      throw new Error(`Invalid connection data: ${validation.errors.join(', ')}`);
    }
    
    const normalizedData = {
      host: rawData.host || rawData.ip || '',
      username: rawData.username || '',
      password: rawData.password || '',
      port: rawData.port || 22,
      serverName: rawData.serverName || rawData.name || rawData.host || 'Unknown Server',
      
      userId: rawData.userId || storedUserData.userId || '',
      userName: rawData.userName || storedUserData.userName || '',
      userEmail: rawData.userEmail || storedUserData.userEmail || '',
      registeredId: rawData.registeredId || storedUserData.registeredId || '',
      userRole: rawData.userRole || storedUserData.userRole || 'student',
      
      token: rawData.token || storedUserData.token || '',
      sessionId: rawData.sessionId || `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      connectionTime: rawData.connectionTime || new Date().toISOString(),
      
      dashboardUrl: rawData.dashboardUrl || window.location.origin,
      browserInfo: rawData.browserInfo || navigator.userAgent,
      serverId: rawData.serverId || rawData.id || rawData._id || '',
      
      user: rawData.user || {
        id: rawData.userId || storedUserData.userId,
        name: rawData.userName || storedUserData.userName,
        email: rawData.userEmail || storedUserData.userEmail,
        registered_id: rawData.registeredId || storedUserData.registeredId,
        role: rawData.userRole || storedUserData.userRole || 'student'
      }
    };

    console.log("âœ… Normalized and validated connection data:", normalizedData);
    return normalizedData;
  }, [getStoredUserData]);

  // Enhanced connection initialization
  const initializeConnection = useCallback(async () => {
    if (isInitializedRef.current) {
      console.log("Connection already initialized, skipping...");
      return;
    }
    
    try {
      const urlParams = new URLSearchParams(window.location.search);
      let data = null;

      // Method 1: SessionKey approach (most secure)
      const sessionKey = urlParams.get('sessionKey');
      if (sessionKey) {
        const sessionData = sessionStorage.getItem(sessionKey);
        if (sessionData) {
          data = JSON.parse(sessionData);
          sessionStorage.removeItem(sessionKey);
        }
      }

      // Method 2: Direct URL data
      if (!data) {
        const urlData = urlParams.get('data') || urlParams.get('fallbackData');
        if (urlData) {
          data = JSON.parse(decodeURIComponent(urlData));
        }
      }

      // Method 3: Individual URL parameters
      if (!data) {
        const host = urlParams.get('host');
        const username = urlParams.get('username');
        const password = urlParams.get('password');
        const serverName = urlParams.get('serverName') || urlParams.get('name');
        const token = urlParams.get('token');
        const userId = urlParams.get('userId');
        const serverId = urlParams.get('serverId');

        if (host && username && password) {
          data = {
            host, username, password, serverName, token, userId, serverId,
            ...getStoredUserData()
          };
        }
      }

      // Method 4: SessionStorage fallback
      if (!data) {
        const stored = sessionStorage.getItem('terminalConnection');
        if (stored) {
          data = JSON.parse(stored);
        }
      }

      if (data) {
        const normalizedData = parseConnectionData(data);
        setConnectionData(normalizedData);
        
        // Fetch complete server data from database
        await fetchCompleteServerData(normalizedData.serverId, normalizedData.host);
      } else {
        setError("No connection data provided. Please return to the dashboard and try again.");
      }
    } catch (err) {
      console.error("âŒ Error initializing connection:", err);
      setError(`Failed to initialize connection: ${err.message}`);
    }
  }, [getStoredUserData, parseConnectionData, fetchCompleteServerData]);

  // Event handlers
  const handleDisconnect = useCallback(async () => {
    if (globalSocketInstance && connectionData) {
      globalSocketInstance.emit("user-disconnect", {
        userId: connectionData.userId,
        userName: connectionData.userName,
        sessionId: connectionData.sessionId,
        serverId: connectionData.serverId,
        disconnectTime: new Date().toISOString(),
        disconnectReason: 'user_initiated',
        connectionStats: connectionStats
      });
      globalSocketInstance.disconnect();
    }
    
    await cleanupConnection();
    window.close();
  }, [connectionData, cleanupConnection, connectionStats]);

  const handleReconnect = useCallback(() => {
    if (connectionData) {
      setError("");
      setConnectionStatus("reconnecting");
      
      if (globalInputHandler) {
        globalInputHandler.dispose();
        globalInputHandler = null;
      }
      if (globalKeyHandler) {
        globalKeyHandler.dispose();
        globalKeyHandler = null;
      }
      
      globalIsInitialized = false;
      globalEventListenersAttached = false;
      isInitializedRef.current = false;
      
      if (globalTerminalInstance && !globalTerminalInstance.isDisposed) {
        globalTerminalInstance.clear();
        globalTerminalInstance.write("\u001b[36mðŸ”„ Reconnecting...\u001b[0m\r\n");
      }
      
      if (globalSocketInstance) {
        globalSocketInstance.removeAllListeners();
        globalSocketInstance.disconnect();
        globalSocketInstance = null;
      }
      
      if (globalTerminalInstance) {
        globalTerminalInstance.dispose();
        globalTerminalInstance = null;
      }
      
      setTimeout(() => {
        initializeTerminal(connectionData);
      }, 1000);
    }
  }, [connectionData, initializeTerminal]);

  const handleNewSession = useCallback(() => {
    if (globalTerminalInstance && !globalTerminalInstance.isDisposed) {
      globalTerminalInstance.clear();
    }
    if (globalSocketInstance && connectionData) {
      const newSessionData = {
        ...connectionData,
        sessionId: `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        connectionTime: new Date().toISOString()
      };
      setConnectionData(newSessionData);
      setConnectionStats({
        startTime: null,
        dataReceived: 0,
        dataSent: 0,
        commandCount: 0
      });
      connectToSSHServer(globalSocketInstance, newSessionData, globalTerminalInstance);
    }
  }, [connectionData, connectToSSHServer]);

  const toggleFullscreen = useCallback(() => {
    setIsFullscreen(!isFullscreen);
    setTimeout(() => {
      if (globalFitAddon && globalTerminalInstance && !globalTerminalInstance.isDisposed) {
        try {
          globalFitAddon.fit();
        } catch (err) {
          console.warn("Fit error after fullscreen toggle:", err);
        }
      }
    }, 100);
  }, [isFullscreen]);

  const changeTheme = useCallback((newTheme) => {
    setTerminalTheme(newTheme);
    if (globalTerminalInstance && !globalTerminalInstance.isDisposed) {
      globalTerminalInstance.options.theme = getTerminalTheme(newTheme);
    }
    
    // Apply theme to root element
    document.documentElement.setAttribute('data-theme', newTheme);
  }, []);

  // Utility functions
  const getStatusColor = () => {
    switch (connectionStatus) {
      case "connected":
      case "ssh-connected":
        return "var(--success-color)";
      case "connecting":
      case "reconnecting":
        return "var(--warning-color)";
      case "error":
        return "var(--error-color)";
      default:
        return "var(--muted-color)";
    }
  };

  const getStatusText = () => {
    switch (connectionStatus) {
      case "initializing": return "Initializing...";
      case "connecting": return "Connecting...";
      case "connected": return "Socket Connected";
      case "ssh-connected": return "SSH Connected";
      case "reconnecting": return "Reconnecting...";
      case "disconnected": return "Disconnected";
      case "error": return "Connection Error";
      default: return "Unknown";
    }
  };

  const getUserDisplayInfo = () => {
    if (!connectionData) return { name: 'Unknown', id: 'N/A', email: 'N/A', role: 'student' };
    
    return {
      name: connectionData.userName || 'Unknown',
      id: connectionData.registeredId || 'N/A',
      email: connectionData.userEmail || 'N/A',
      role: connectionData.userRole || 'student'
    };
  };

  const formatBytes = (bytes) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const formatDuration = (startTime) => {
    if (!startTime) return '0s';
    const duration = Math.floor((new Date() - startTime) / 1000);
    const hours = Math.floor(duration / 3600);
    const minutes = Math.floor((duration % 3600) / 60);
    const seconds = duration % 60;
    
    if (hours > 0) return `${hours}h ${minutes}m ${seconds}s`;
    if (minutes > 0) return `${minutes}m ${seconds}s`;
    return `${seconds}s`;
  };

  // Effects
  useEffect(() => {
    if (!isInitializedRef.current) {
      initializeConnection();
    }
  }, [initializeConnection]);

  useEffect(() => {
    if (connectionData && !globalIsInitialized) {
      const cleanup = initializeTerminal(connectionData);
      return cleanup;
    } else if (connectionData && globalIsInitialized) {
      if (globalTerminalInstance && terminalRef.current && !globalTerminalInstance.isDisposed) {
        try {
          globalTerminalInstance.open(terminalRef.current);
          terminalInstance.current = globalTerminalInstance;
          socketRef.current = globalSocketInstance;
          setIsConnected(!!globalSocketInstance?.connected);
          setConnectionStatus(globalSocketInstance?.connected ? "ssh-connected" : "connected");
        } catch (err) {
          console.error("Error reconnecting to existing terminal:", err);
        }
      }
    }
  }, [connectionData, initializeTerminal]);

  useEffect(() => {
    const handleMessage = (event) => {
      if (event.data && event.data.type === 'TERMINAL_CONNECTION_DATA') {
        if (!isInitializedRef.current) {
          const normalizedData = parseConnectionData(event.data.data);
          setConnectionData(normalizedData);
          fetchCompleteServerData(normalizedData.serverId, normalizedData.host);
        }
      }
    };
    
    window.addEventListener('message', handleMessage);
    return () => window.removeEventListener('message', handleMessage);
  }, [parseConnectionData, fetchCompleteServerData]);

  useEffect(() => {
    return () => {
      cleanupConnection();
    };
  }, [cleanupConnection]);

  // Apply theme on mount
  useEffect(() => {
    document.documentElement.setAttribute('data-theme', terminalTheme);
  }, [terminalTheme]);

  // Error screen
  if (error && !connectionData) {
    return (
      <div className="terminal-error-screen">
        <div className="error-container">
          <div className="error-icon">âš ï¸</div>
          <h2 className="error-title">Terminal Connection Error</h2>
          <p className="error-message">{error}</p>
          <p className="error-subtitle">Please return to the dashboard and try connecting again.</p>
          <div className="error-actions">
            <button className="error-button" onClick={() => window.close()}>
              Close Window
            </button>
            <button className="loading-button" onClick={initializeConnection}>
              Retry Connection
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Loading screen
  if (!connectionData) {
    return (
      <div className="terminal-loading-screen">
        <div className="loading-container">
          <div className="loading-icon">ðŸ”„</div>
          <h2 className="loading-title">Loading Terminal...</h2>
          <p className="loading-message">Initializing connection and fetching server data...</p>
          <button className="loading-button" onClick={initializeConnection}>
            ðŸ”„ Retry Connection
          </button>
        </div>
      </div>
    );
  }

  const userInfo = getUserDisplayInfo();

  return (
    <div className={`terminal-app ${isFullscreen ? 'fullscreen' : ''}`} data-theme={terminalTheme} data-status={connectionStatus}>
      {/* Enhanced Header */}
      <div className="terminal-header">
        <div className="header-left">
          <div className="server-info">
            <h3 className="server-name">
              ðŸ–¥ï¸ {serverData?.name || connectionData?.serverName || connectionData?.host || "Terminal"}
            </h3>
            <div className="status-indicator" style={{ backgroundColor: getStatusColor() }}>
              <div className="status-dot"></div>
              {getStatusText()}
            </div>
          </div>
          <div className="user-info">
            ðŸ‘¤ {userInfo.name} 
            {userInfo.id !== 'N/A' && ` (ID: ${userInfo.id})`}
            {userInfo.email !== 'N/A' && ` | ðŸ“§ ${userInfo.email}`}
            {connectionData && ` | ðŸ”— ${connectionData.username}@${connectionData.host}`}
          </div>
        </div>

        <div className="header-controls">
          <div className="connection-stats">
            <span>â±ï¸ {formatDuration(connectionStats.startTime)}</span>
            <span>ðŸ“Š {connectionStats.commandCount} cmds</span>
          </div>
          
          <div className="theme-selector">
            <select 
              value={terminalTheme} 
              onChange={(e) => changeTheme(e.target.value)}
              className="theme-select"
            >
              <option value="dark">Dark</option>
              <option value="light">Light</option>
              <option value="matrix">Matrix</option>
            </select>
          </div>
          
          <button 
            className="control-button info-btn" 
            onClick={() => setShowServerPanel(!showServerPanel)}
            title="Server Information"
          >
            ðŸ“Š
          </button>
          
          <button 
            className="control-button fullscreen-btn" 
            onClick={toggleFullscreen}
            title={isFullscreen ? "Exit Fullscreen" : "Enter Fullscreen"}
          >
            {isFullscreen ? "ðŸ——" : "ðŸ—–"}
          </button>

          {(connectionStatus === "error" || connectionStatus === "disconnected") && (
            <button className="control-button reconnect-btn" onClick={handleReconnect}>
              ðŸ”„ Reconnect
            </button>
          )}
          
          <button className="control-button new-session-btn" onClick={handleNewSession}>
            ðŸ†• New Session
          </button>
          
          <button className="control-button disconnect-btn" onClick={handleDisconnect}>
            âŒ Disconnect
          </button>
        </div>
      </div>

      {/* Enhanced Connection Info Bar */}
      <div className="connection-info-bar">
        <div className="connection-details">
          <span>ðŸŒ {connectionData.host}:{connectionData.port}</span>
          <span>ðŸ‘¥ {userInfo.role}</span>
          <span>ðŸ”‘ {connectionData.sessionId?.substr(-8) || 'N/A'}</span>
          {connectionData.serverId && <span>ðŸ†” {connectionData.serverId.substr(-8)}</span>}
          {serverData && (
            <>
              {serverData.location && <span>ðŸ“ {serverData.location}</span>}
              {serverData.os && <span>ðŸ’¾ {serverData.os}</span>}
              {serverData.max_connections && <span>ðŸ‘¥ Max: {serverData.max_connections}</span>}
            </>
          )}
          <span>ðŸ“¤ {formatBytes(connectionStats.dataSent)}</span>
          <span>ðŸ“¥ {formatBytes(connectionStats.dataReceived)}</span>
        </div>
        <div className="connection-time">
          Connected: {connectionData.connectionTime ? new Date(connectionData.connectionTime).toLocaleTimeString() : 'N/A'}
        </div>
      </div>

      {/* Terminal Container */}
      <div className="terminal-container">
        <div 
          ref={terminalRef} 
          className="terminal-content"
        />
      </div>

      {/* Enhanced Status Footer */}
      <div className="terminal-footer">
        <div className="footer-left">
          {error ? (
            <span className="footer-error">âŒ Error: {error}</span>
          ) : (
            <span className="footer-info">
              Terminal ready | Ctrl+C to interrupt | Type 'exit' to close | Theme: {terminalTheme}
            </span>
          )}
        </div>
        <div className="footer-right">
          <span>User: {userInfo.name}</span>
          {serverData?.name && <span> | Server: {serverData.name}</span>}
          {serverData?.status && <span> | Status: {serverData.status}</span>}
        </div>
      </div>

      {/* Enhanced Server Details Panel */}
      {showServerPanel && serverData && (
        <div className="server-details-panel slide-in">
          <div className="panel-header">
            <h4>ðŸ“Š Server Information</h4>
            <button className="panel-close" onClick={() => setShowServerPanel(false)}>Ã—</button>
          </div>
          <div className="panel-content">
            <div className="detail-grid">
              <div className="detail-item">
                <span className="detail-label">Name:</span>
                <span className="detail-value">{serverData.name}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">IP Address:</span>
                <span className="detail-value">{serverData.ip}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Port:</span>
                <span className="detail-value">{serverData.port || 22}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Status:</span>
                <span className={`detail-value status-${serverData.status}`}>
                  {serverData.status || 'active'}
                </span>
              </div>
              {serverData.os && (
                <div className="detail-item">
                  <span className="detail-label">Operating System:</span>
                  <span className="detail-value">{serverData.os}</span>
                </div>
              )}
              {serverData.location && (
                <div className="detail-item">
                  <span className="detail-label">Location:</span>
                  <span className="detail-value">{serverData.location}</span>
                </div>
              )}
              {serverData.max_connections && (
                <div className="detail-item">
                  <span className="detail-label">Max Connections:</span>
                  <span className="detail-value">{serverData.max_connections}</span>
                </div>
              )}
              {serverData.version && (
                <div className="detail-item">
                  <span className="detail-label">Version:</span>
                  <span className="detail-value">{serverData.version}</span>
                </div>
              )}
              {serverData.description && (
                <div className="detail-item full-width">
                  <span className="detail-label">Description:</span>
                  <span className="detail-value">{serverData.description}</span>
                </div>
              )}
              {serverData.created_at && (
                <div className="detail-item">
                  <span className="detail-label">Created:</span>
                  <span className="detail-value">
                    {new Date(serverData.created_at).toLocaleDateString()}
                  </span>
                </div>
              )}
              {serverData.updated_at && (
                <div className="detail-item">
                  <span className="detail-label">Last Updated:</span>
                  <span className="detail-value">
                    {new Date(serverData.updated_at).toLocaleDateString()}
                  </span>
                </div>
              )}
            </div>
            
            {/* Connection Statistics */}
            <div className="stats-section">
              <h5>Connection Statistics</h5>
              <div className="stats-grid">
                <div className="stat-item">
                  <span className="stat-label">Session Duration:</span>
                  <span className="stat-value">{formatDuration(connectionStats.startTime)}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Commands Sent:</span>
                  <span className="stat-value">{connectionStats.commandCount}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Data Sent:</span>
                  <span className="stat-value">{formatBytes(connectionStats.dataSent)}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Data Received:</span>
                  <span className="stat-value">{formatBytes(connectionStats.dataReceived)}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TerminalPage;
