import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import { Terminal } from "xterm";
import "xterm/css/xterm.css";

const SERVER_URL = "http://localhost:5000";

const servers = [
  { name: "Server 1", ip: "172.184.216.215" },
  { name: "Server 2", ip: "20.245.171.127" },
];

const StudentDashboard = () => {
  const [selectedServer, setSelectedServer] = useState(servers[0]);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  //const [socket, setSocket] = useState(null);
  const terminalRef = useRef();

  useEffect(() => {
    const newSocket = io(SERVER_URL);
    //setSocket(newSocket);
    const term = new Terminal();
    term.open(terminalRef.current);

    newSocket.on("ssh-output", (data) => {
      term.write(data);
    });

    term.onData((input) => {
      newSocket.emit("ssh-input", input);
    });

    return () => newSocket.disconnect();
  }, []);

  const handleConnect = () => {
  const terminalUrl = `/ssh-terminal.html?host=${selectedServer.ip}&username=${username}&password=${password}`;
  window.open(terminalUrl, "_blank");
};


  return (
    <div>
      <h2>Student Dashboard</h2>
      <select
        value={selectedServer.ip}
        onChange={(e) =>
          setSelectedServer(servers.find((s) => s.ip === e.target.value))
        }
      >
        {servers.map((server) => (
          <option key={server.ip} value={server.ip}>
            {server.name} - {server.ip}
          </option>
        ))}
      </select>

      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      <button onClick={handleConnect}>Connect</button>

      <div
        ref={terminalRef}
        style={{ width: "100%", height: "500px", marginTop: "20px", background: "black" }}
      />
    </div>
  );
};

export default StudentDashboard;
