import React, { useState, useEffect, useCallback } from 'react';
import { 
  Users, 
  Server, 
  Terminal, 
  Clock, 
  Activity, 
  Eye, 
  AlertTriangle,
  RefreshCw,
  Search,
  Filter,
  BarChart3,
  User,
  ArrowLeft,
  Monitor,
  Command as CommandIcon,
  Calendar,
  Globe,
  Shield,
  Zap
} from 'lucide-react';

const AdminDashboard = () => {
  const [dashboardStats, setDashboardStats] = useState(null);
  const [servers, setServers] = useState([]);
  const [students, setStudents] = useState([]);
  const [selectedServer, setSelectedServer] = useState(null);
  const [serverSessions, setServerSessions] = useState([]);
  const [selectedSession, setSelectedSession] = useState(null);
  const [sessionDetails, setSessionDetails] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [currentView, setCurrentView] = useState('overview');
  const [searchTerm, setSearchTerm] = useState('');

  const API_BASE = 'http://localhost:5000/api';
  
  const getAuthHeaders = () => ({
    'Authorization': `Bearer ${localStorage.getItem('token')}`,
    'Content-Type': 'application/json'
  });

  // Fetch dashboard statistics
  const fetchDashboardStats = useCallback(async () => {
    try {
      const response = await fetch(`${API_BASE}/admin/dashboard/stats`, {
        headers: getAuthHeaders()
      });
      if (response.ok) {
        const data = await response.json();
        setDashboardStats(data);
      }
    } catch (err) {
      console.error('Error fetching dashboard stats:', err);
    }
  }, []);

  // Fetch servers list
  const fetchServers = useCallback(async () => {
    try {
      const response = await fetch(`${API_BASE}/admin/servers`, {
        headers: getAuthHeaders()
      });
      if (response.ok) {
        const data = await response.json();
        setServers(data);
      }
    } catch (err) {
      console.error('Error fetching servers:', err);
    }
  }, []);

  // Fetch students list
  const fetchStudents = useCallback(async () => {
    try {
      const response = await fetch(`${API_BASE}/admin/students`, {
        headers: getAuthHeaders()
      });
      if (response.ok) {
        const data = await response.json();
        setStudents(data);
      }
    } catch (err) {
      console.error('Error fetching students:', err);
    }
  }, []);

  // Fetch server sessions
  const fetchServerSessions = useCallback(async (serverIp) => {
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE}/admin/servers/${serverIp}/sessions?limit=10`, {
        headers: getAuthHeaders()
      });
      if (response.ok) {
        const data = await response.json();
        setServerSessions(data.sessions);
        setSelectedServer(serverIp);
        setCurrentView('servers');
      }
    } catch (err) {
      setError('Error fetching server sessions');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  // Fetch session details
  const fetchSessionDetails = useCallback(async (sessionId) => {
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE}/admin/sessions/${sessionId}`, {
        headers: getAuthHeaders()
      });
      if (response.ok) {
        const data = await response.json();
        setSessionDetails(data);
        setSelectedSession(sessionId);
        setCurrentView('session-details');
      }
    } catch (err) {
      setError('Error fetching session details');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  // Initial data load
  useEffect(() => {
    fetchDashboardStats();
    fetchServers();
    fetchStudents();
  }, [fetchDashboardStats, fetchServers, fetchStudents]);

  // Auto-refresh every 30 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      fetchDashboardStats();
      fetchServers();
    }, 30000);
    return () => clearInterval(interval);
  }, [fetchDashboardStats, fetchServers]);

  const handleRefresh = () => {
    fetchDashboardStats();
    fetchServers();
    fetchStudents();
  };

  const formatDuration = (seconds) => {
    if (!seconds) return '0s';
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    
    if (hours > 0) return `${hours}h ${minutes}m`;
    if (minutes > 0) return `${minutes}m ${secs}s`;
    return `${secs}s`;
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleString();
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return '#10b981';
      case 'disconnected': return '#6b7280';
      case 'timeout': return '#f59e0b';
      default: return '#6b7280';
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const filteredServers = servers.filter(server => 
    server.server_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    server.server_ip?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const filteredStudents = students.filter(student =>
    student.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.registered_id?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // CSS Styles
  const styles = `
    .admin-dashboard {
      min-height: 100vh;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    }

    .dashboard-header {
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(10px);
      border-bottom: 1px solid rgba(255, 255, 255, 0.2);
      padding: 1rem 2rem;
      display: flex;
      justify-content: space-between;
      align-items: center;
      box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    }

    .dashboard-title {
      color: white;
      margin: 0;
      font-size: 1.8rem;
      font-weight: 600;
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }

    .dashboard-controls {
      display: flex;
      align-items: center;
      gap: 1rem;
    }

    .btn {
      padding: 0.5rem 1rem;
      border: none;
      border-radius: 8px;
      cursor: pointer;
      font-weight: 500;
      transition: all 0.2s ease;
      display: flex;
      align-items: center;
      gap: 0.5rem;
      text-decoration: none;
    }

    .btn-primary {
      background: rgba(59, 130, 246, 0.8);
      color: white;
    }

    .btn-primary:hover {
      background: rgba(18, 86, 196, 1);
      transform: translateY(-1px);
    }

    .btn-secondary {
      background: rgba(255, 255, 255, 0.1);
      color: white;
      border: 1px solid rgba(255, 255, 255, 0.2);
    }

    .btn-secondary:hover {
      background: rgba(255, 255, 255, 0.2);
    }

    .btn-danger {
      background: rgba(239, 68, 68, 0.8);
      color: white;
    }

    .btn-danger:hover {
      background: rgba(239, 68, 68, 1);
    }

    .dashboard-content {
      padding: 2rem;
      max-width: 1400px;
      margin: 0 auto;
    }

    .navigation-tabs {
      display: flex;
      gap: 0.5rem;
      margin-bottom: 2rem;
      background: rgba(255, 255, 255, 0.1);
      border-radius: 12px;
      padding: 0.5rem;
    }

    .nav-tab {
      padding: 0.75rem 1.5rem;
      border-radius: 8px;
      border: none;
      background: transparent;
      color: rgba(243, 243, 243, 1);
      cursor: pointer;
      transition: all 0.2s ease;
      display: flex;
      align-items: center;
      gap: 0.5rem;
      font-weight: 500;
    }

    .nav-tab.active {
      background: rgba(255, 255, 255, 0.2);
      color: white;
      backdrop-filter: blur(10px);
    }

    .nav-tab:hover:not(.active) {
      background: rgba(255, 255, 255, 0.1);
      color: rgba(255, 255, 255, 0.9);
    }

    .stats-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
      gap: 1.5rem;
      margin-bottom: 2rem;
    }

    .stat-card {
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(10px);
      border: 1px solid rgba(255, 255, 255, 0.2);
      border-radius: 16px;
      padding: 1.5rem;
      color: white;
      transition: transform 0.2s ease, box-shadow 0.2s ease;
    }

    .stat-card:hover {
      transform: translateY(-4px);
      box-shadow: 0 8px 25px rgba(0, 0, 0, 0.2);
    }

    .stat-header {
      display: flex;
      align-items: center;
      justify-content: space-between;
      margin-bottom: 1rem;
    }

    .stat-icon {
      padding: 0.75rem;
      border-radius: 12px;
      background: rgba(255, 255, 255, 0.1);
    }

    .stat-value {
      font-size: 2rem;
      font-weight: 700;
      margin-bottom: 0.25rem;
    }

    .stat-label {
      color: rgba(255, 255, 255, 0.8);
      font-size: 0.9rem;
    }

    .search-bar {
      display: flex;
      align-items: center;
      gap: 1rem;
      margin-bottom: 2rem;
    }

    .search-input {
      flex: 1;
      padding: 0.75rem 1rem;
      border: 1px solid rgba(255, 255, 255, 0.2);
      border-radius: 12px;
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(10px);
      color: white;
      font-size: 1rem;
    }

    .search-input::placeholder {
      color: rgba(255, 255, 255, 0.6);
    }

    .search-input:focus {
      outline: none;
      border-color: rgba(59, 130, 246, 0.5);
      box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
    }

    .content-grid {
      display: grid;
      gap: 1.5rem;
    }

    .servers-grid {
      display: grid;
      grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
      gap: 1.5rem;
    }

    .server-card {
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(10px);
      border: 1px solid rgba(255, 255, 255, 0.2);
      border-radius: 16px;
      padding: 1.5rem;
      color: white;
      cursor: pointer;
      transition: all 0.2s ease;
    }

    .server-card:hover {
      transform: translateY(-4px);
      box-shadow: 0 8px 25px rgba(0, 0, 0, 0.2);
      background: rgba(255, 255, 255, 0.15);
    }

    .server-header {
      display: flex;
      align-items: center;
      gap: 1rem;
      margin-bottom: 1rem;
    }

    .server-status {
      display: flex;
      align-items: center;
      gap: 0.5rem;
      font-size: 0.875rem;
    }

    .status-dot {
      width: 8px;
      height: 8px;
      border-radius: 50%;
      background: #10b981;
    }

    .server-stats {
      display: grid;
      grid-template-columns: repeat(2, 1fr);
      gap: 1rem;
      margin-top: 1rem;
    }

    .server-stat {
      text-align: center;
    }

    .server-stat-value {
      font-size: 1.25rem;
      font-weight: 600;
      color: #ffffffff;
    }

    .server-stat-label {
      font-size: 0.75rem;
      color: rgba(255, 255, 255, 0.7);
    }

    .session-table {
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(10px);
      border: 1px solid rgba(255, 255, 255, 0.2);
      border-radius: 16px;
      overflow: hidden;
    }

    .table-header {
      background: rgba(255, 255, 255, 0.1);
      padding: 1rem;
      border-bottom: 1px solid rgba(255, 255, 255, 0.2);
      display: flex;
      align-items: center;
      justify-content: space-between;
    }

    .table {
      width: 100%;
      color: white;
    }

    .table thead {
      background: rgba(255, 255, 255, 0.05);
    }

    .table th,
    .table td {
      padding: 1rem;
      text-align: left;
      border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    }

    .table th {
      font-weight: 600;
      color: rgba(255, 255, 255, 0.9);
      font-size: 0.875rem;
      text-transform: uppercase;
      letter-spacing: 0.05em;
    }

    .table tbody tr {
      cursor: pointer;
      transition: background-color 0.2s ease;
    }

    .table tbody tr:hover {
      background: rgba(255, 255, 255, 0.05);
    }

    .status-badge {
      display: inline-flex;
      align-items: center;
      gap: 0.25rem;
      padding: 0.25rem 0.75rem;
      border-radius: 20px;
      font-size: 0.75rem;
      font-weight: 500;
    }

    .status-active {
      background: rgba(16, 185, 129, 0.2);
      color: #10b981;
      border: 1px solid rgba(16, 185, 129, 0.3);
    }

    .status-disconnected {
      background: rgba(107, 114, 128, 0.2);
      color: #9ca3af;
      border: 1px solid rgba(107, 114, 128, 0.3);
    }

    .session-details {
      display: grid;
      gap: 1.5rem;
    }

    .session-info-card {
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(10px);
      border: 1px solid rgba(255, 255, 255, 0.2);
      border-radius: 16px;
      padding: 1.5rem;
      color: white;
    }

    .session-info-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
      gap: 1rem;
    }

    .info-item {
      display: flex;
      flex-direction: column;
      gap: 0.25rem;
    }

    .info-label {
      font-size: 0.75rem;
      color: rgba(255, 255, 255, 0.7);
      text-transform: uppercase;
      letter-spacing: 0.05em;
    }

    .info-value {
      font-size: 1rem;
      font-weight: 500;
    }

    .commands-section {
      background: rgba(255, 255, 255, 0.1);
      backdrop-filter: blur(10px);
      border: 1px solid rgba(255, 255, 255, 0.2);
      border-radius: 16px;
      padding: 1.5rem;
      color: white;
    }

    .command-item {
      background: rgba(0, 0, 0, 0.2);
      border-radius: 8px;
      padding: 1rem;
      margin-bottom: 1rem;
      border-left: 4px solid #60a5fa;
    }

    .command-header {
      display: flex;
      justify-content: between;
      align-items: center;
      margin-bottom: 0.5rem;
    }

    .command-text {
      font-family: 'Courier New', monospace;
      font-weight: 600;
      color: #60a5fa;
      flex: 1;
    }

    .command-time {
      font-size: 0.75rem;
      color: rgba(255, 255, 255, 0.6);
    }

    .command-output {
      background: rgba(0, 0, 0, 0.3);
      border-radius: 4px;
      padding: 0.75rem;
      margin-top: 0.5rem;
      font-family: 'Courier New', monospace;
      font-size: 0.875rem;
      white-space: pre-wrap;
      max-height: 200px;
      overflow-y: auto;
      border: 1px solid rgba(255, 255, 255, 0.1);
    }

    .loading-spinner {
      display: flex;
      justify-content: center;
      align-items: center;
      padding: 2rem;
      color: white;
    }

    .error-message {
      background: rgba(239, 68, 68, 0.1);
      border: 1px solid rgba(239, 68, 68, 0.3);
      color: #f87171;
      padding: 1rem;
      border-radius: 12px;
      margin-bottom: 1rem;
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }

    .breadcrumb {
      display: flex;
      align-items: center;
      gap: 0.5rem;
      margin-bottom: 1rem;
      color: rgba(255, 255, 255, 0.8);
    }

    .breadcrumb-item {
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }

    .breadcrumb-separator {
      color: rgba(255, 255, 255, 0.5);
    }

    @media (max-width: 768px) {
      .dashboard-header {
        flex-direction: column;
        gap: 1rem;
        text-align: center;
      }

      .dashboard-controls {
        flex-wrap: wrap;
        justify-content: center;
      }

      .stats-grid {
        grid-template-columns: 1fr;
      }

      .servers-grid {
        grid-template-columns: 1fr;
      }

      .navigation-tabs {
        flex-wrap: wrap;
      }

      .search-bar {
        flex-direction: column;
        align-items: stretch;
      }
    }

    .animate-pulse {
      animation: pulse 2s infinite;
    }

    @keyframes pulse {
      0%, 100% { opacity: 1; }
      50% { opacity: 0.5; }
    }
  `;

  const renderOverview = () => (
    <div className="content-grid">
      {dashboardStats && (
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-header">
              <div className="stat-icon">
                <Users size={24} />
              </div>
            </div>
            <div className="stat-value">{dashboardStats.overview.total_students}</div>
            <div className="stat-label">Total Students</div>
          </div>
          
          <div className="stat-card">
            <div className="stat-header">
              <div className="stat-icon">
                <Terminal size={24} />
              </div>
            </div>
            <div className="stat-value">{dashboardStats.overview.total_sessions}</div>
            <div className="stat-label">Total Sessions</div>
          </div>
          
          <div className="stat-card">
            <div className="stat-header">
              <div className="stat-icon">
                <Activity size={24} />
              </div>
            </div>
            <div className="stat-value">{dashboardStats.overview.active_sessions}</div>
            <div className="stat-label">Active Sessions</div>
          </div>
          
          <div className="stat-card">
            <div className="stat-header">
              <div className="stat-icon">
                <CommandIcon size={24} />
              </div>
            </div>
            <div className="stat-value">{dashboardStats.overview.total_commands}</div>
            <div className="stat-label">Commands Executed</div>
          </div>
        </div>
      )}

      <div className="servers-grid">
        {servers.map((server) => (
          <div 
            key={server.server_ip} 
            className="server-card"
            onClick={() => fetchServerSessions(server.server_ip)}
          >
            <div className="server-header">
              <div className="stat-icon">
                <Server size={20} />
              </div>
              <div>
                <h3 style={{margin: 0, fontSize: '1.1rem'}}>{server.server_name}</h3>
                <p style={{margin: 0, fontSize: '0.875rem', opacity: 0.8}}>{server.server_ip}</p>
              </div>
            </div>
            
            <div className="server-status">
              <div className="status-dot"></div>
              <span>Online  Last activity: {formatDateTime(server.last_activity)}</span>
            </div>
            
            <div className="server-stats">
              <div className="server-stat">
                <div className="server-stat-value">{server.total_sessions}</div>
                <div className="server-stat-label">Total Sessions</div>
              </div>
              <div className="server-stat">
                <div className="server-stat-value">{server.active_sessions}</div>
                <div className="server-stat-label">Active Now</div>
              </div>
              <div className="server-stat">
                <div className="server-stat-value">{server.unique_user_count}</div>
                <div className="server-stat-label">Unique Users</div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const renderServerSessions = () => (
    <div className="content-grid">
      <div className="breadcrumb">
        <button className="btn btn-secondary" onClick={() => setCurrentView('overview')}>
          <ArrowLeft size={16} />
          Back to Overview
        </button>
        <span className="breadcrumb-separator"></span>
        <span>Server: {selectedServer}</span>
      </div>

      <div className="session-table">
        <div className="table-header">
          <h3 style={{margin: 0, display: 'flex', alignItems: 'center', gap: '0.5rem'}}>
            <Terminal size={20} />
            Server Sessions - {selectedServer}
          </h3>
          <button className="btn btn-primary" onClick={() => fetchServerSessions(selectedServer)}>
            <RefreshCw size={16} />
            Refresh
          </button>
        </div>
        
        {loading ? (
          <div className="loading-spinner">
            <RefreshCw size={24} className="animate-pulse" />
            Loading sessions...
          </div>
        ) : (
          <table className="table">
            <thead>
              <tr>
                <th>Student</th>
                <th>Email</th>
                <th>ID</th>
                <th>Login Time</th>
                <th>Duration</th>
                <th>Commands</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {serverSessions.map((session) => (
                <tr key={session.id} onClick={() => fetchSessionDetails(session.id)}>
                  <td>{session.user.name}</td>
                  <td>{session.user.email}</td>
                  <td>{session.user.registered_id}</td>
                  <td>{formatDateTime(session.session_details.login_time)}</td>
                  <td>{formatDuration(session.session_details.session_duration)}</td>
                  <td>{session.commands_count}</td>
                  <td>
                    <span className={`status-badge ${session.session_details.status === 'active' ? 'status-active' : 'status-disconnected'}`}>
                      <div style={{width: '6px', height: '6px', borderRadius: '50%', backgroundColor: getStatusColor(session.session_details.status)}}></div>
                      {session.session_details.status}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );

  const renderSessionDetails = () => (
    <div className="session-details">
      <div className="breadcrumb">
        <button className="btn btn-secondary" onClick={() => setCurrentView('servers')}>
          <ArrowLeft size={16} />
          Back to Sessions
        </button>
        <span className="breadcrumb-separator"></span>
        <span>Session Details</span>
      </div>

      {sessionDetails && (
        <>
          <div className="session-info-card">
            <h3 style={{marginTop: 0, display: 'flex', alignItems: 'center', gap: '0.5rem'}}>
              <User size={20} />
              Session Information
            </h3>
            <div className="session-info-grid">
              <div className="info-item">
                <span className="info-label">Student Name</span>
                <span className="info-value">{sessionDetails.user.name}</span>
              </div>
              <div className="info-item">
                <span className="info-label">Email</span>
                <span className="info-value">{sessionDetails.user.email}</span>
              </div>
              <div className="info-item">
                <span className="info-label">Student ID</span>
                <span className="info-value">{sessionDetails.user.registered_id}</span>
              </div>
              <div className="info-item">
                <span className="info-label">Server</span>
                <span className="info-value">{sessionDetails.server_info.name}</span>
              </div>
              <div className="info-item">
                <span className="info-label">Server IP</span>
                <span className="info-value">{sessionDetails.server_info.ip}</span>
              </div>
              <div className="info-item">
                <span className="info-label">SSH User</span>
                <span className="info-value">{sessionDetails.server_info.ssh_username}</span>
              </div>
              <div className="info-item">
                <span className="info-label">Login Time</span>
                <span className="info-value">{formatDateTime(sessionDetails.session_details.login_time)}</span>
              </div>
              <div className="info-item">
                <span className="info-label">Logout Time</span>
                <span className="info-value">{formatDateTime(sessionDetails.session_details.logout_time)}</span>
              </div>
              <div className="info-item">
                <span className="info-label">Duration</span>
                <span className="info-value">{formatDuration(sessionDetails.session_details.session_duration)}</span>
              </div>
              <div className="info-item">
                <span className="info-label">Status</span>
                <span className={`status-badge ${sessionDetails.session_details.status === 'active' ? 'status-active' : 'status-disconnected'}`}>
                  {sessionDetails.session_details.status}
                </span>
              </div>
            </div>
          </div>

          <div className="commands-section">
            <h3 style={{marginTop: 0, display: 'flex', alignItems: 'center', gap: '0.5rem'}}>
              <CommandIcon size={20} />
              Commands Executed ({sessionDetails.commands.length})
            </h3>
            
            {sessionDetails.commands.length === 0 ? (
              <p style={{color: 'rgba(255, 255, 255, 0.6)', fontStyle: 'italic'}}>No commands executed in this session.</p>
            ) : (
              <div style={{maxHeight: '600px', overflowY: 'auto'}}>
                {sessionDetails.commands.map((command, index) => (
                  <div key={command.id} className="command-item">
                    <div className="command-header">
                      <span className="command-text">$ {command.command}</span>
                      <span className="command-time">
                        {formatDateTime(command.executed_at)}
                      </span>
                    </div>
                    {command.output && (
                      <div className="command-output">
                        {command.output}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );

  const renderStudents = () => (
    <div className="content-grid">
      <div className="search-bar">
        <div style={{position: 'relative', flex: 1}}>
          <Search size={20} style={{position: 'absolute', left: '12px', top: '50%', transform: 'translateY(-50%)', color: 'rgba(255, 255, 255, 0.6)'}} />
          <input
            type="text"
            placeholder="Search students by name, email, or ID..."
            className="search-input"
            style={{paddingLeft: '44px'}}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <button className="btn btn-primary" onClick={fetchStudents}>
          <RefreshCw size={16} />
          Refresh
        </button>
      </div>

      <div className="session-table">
        <div className="table-header">
          <h3 style={{margin: 0, display: 'flex', alignItems: 'center', gap: '0.5rem'}}>
            <Users size={20} />
            Students ({filteredStudents.length})
          </h3>
        </div>
        
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Student ID</th>
              <th>Total Sessions</th>
              <th>Active Sessions</th>
              <th>Total Time</th>
              <th>Last Activity</th>
            </tr>
          </thead>
          <tbody>
            {filteredStudents.map((student) => (
              <tr key={student.id}>
                <td style={{fontWeight: '500'}}>{student.name}</td>
                <td>{student.email}</td>
                <td>{student.registered_id}</td>
                <td>{student.statistics.total_sessions}</td>
                <td>
                  <span className={`status-badge ${student.statistics.active_sessions > 0 ? 'status-active' : 'status-disconnected'}`}>
                    {student.statistics.active_sessions}
                  </span>
                </td>
                <td>{formatDuration(student.statistics.total_duration)}</td>
                <td>{formatDateTime(student.statistics.last_activity)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  return (
    <>
      <style>{styles}</style>
      <div className="admin-dashboard">
        <div className="dashboard-header">
          <h1 className="dashboard-title">
            <Shield size={28} />
            Admin Dashboard
          </h1>
          <div className="dashboard-controls">
            <button className="btn btn-secondary" onClick={handleRefresh}>
              <RefreshCw size={16} />
              Refresh All
            </button>
            <button className="btn btn-danger" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>

        <div className="dashboard-content">
          {error && (
            <div className="error-message">
              <AlertTriangle size={16} />
              {error}
            </div>
          )}

          <div className="navigation-tabs">
            <button 
              className={`nav-tab ${currentView === 'overview' ? 'active' : ''}`}
              onClick={() => setCurrentView('overview')}
            >
              <BarChart3 size={16} />
              Overview
            </button>
            <button 
              className={`nav-tab ${currentView === 'servers' ? 'active' : ''}`}
              onClick={() => setCurrentView('overview')}
            >
              <Server size={16} />
              Servers
            </button>
            <button 
              className={`nav-tab ${currentView === 'students' ? 'active' : ''}`}
              onClick={() => setCurrentView('students')}
            >
              <Users size={16} />
              Students
            </button>
          </div>

          {currentView === 'overview' && renderOverview()}
          {currentView === 'servers' && renderServerSessions()}
          {currentView === 'session-details' && renderSessionDetails()}
          {currentView === 'students' && renderStudents()}
        </div>
      </div>
    </>
  );
};

export default AdminDashboard;
