import React, { useState } from "react";

const servers = [
  { name: "Server 1", ip: "172.184.216.215" },
  { name: "Server 2", ip: "20.245.171.127" },
];

const StudentDashboard = () => {
  const [selectedServer, setSelectedServer] = useState(servers[0]);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isConnecting, setIsConnecting] = useState(false);

  const handleConnect = () => {
    if (!username || !password) {
      alert("Please enter both username and password");
      return;
    }

    setIsConnecting(true);

    // Method 1: Using URL parameters (recommended)
    const params = new URLSearchParams({
      host: selectedServer.ip,
      username: username,
      password: password,
      serverName: selectedServer.name
    });

    // Method 2: Store in sessionStorage as backup
    sessionStorage.setItem('terminalConnection', JSON.stringify({
      host: selectedServer.ip,
      username: username,
      password: password,
      serverName: selectedServer.name
    }));

    // Open new window with terminal page
    const newWindow = window.open(
      `/terminal?${params.toString()}`, 
      `terminal_${selectedServer.ip}`,
      'width=1200,height=800,scrollbars=yes,resizable=yes'
    );

    // Check if popup was blocked
    if (!newWindow) {
      alert("Popup blocked! Please allow popups for this site and try again.");
      setIsConnecting(false);
      return;
    }

    // Reset connecting state after a short delay
    setTimeout(() => {
      setIsConnecting(false);
    }, 2000);

    // Optional: Clear password for security
    setPassword("");
  };

  return (
    <div style={{ 
      maxWidth: '600px', 
      margin: '50px auto', 
      padding: '20px',
      fontFamily: 'Arial, sans-serif'
    }}>
      <h2>Student Dashboard</h2>
      
      <div style={{ marginBottom: '20px' }}>
        <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
          Select Server:
        </label>
        <select
          value={selectedServer.ip}
          onChange={(e) =>
            setSelectedServer(servers.find((s) => s.ip === e.target.value))
          }
          style={{
            width: '100%',
            padding: '10px',
            fontSize: '16px',
            border: '1px solid #ccc',
            borderRadius: '4px'
          }}
        >
          {servers.map((server) => (
            <option key={server.ip} value={server.ip}>
              {server.name} - {server.ip}
            </option>
          ))}
        </select>
      </div>

      <div style={{ marginBottom: '20px' }}>
        <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
          Username:
        </label>
        <input
          type="text"
          placeholder="Enter username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{
            width: '100%',
            padding: '10px',
            fontSize: '16px',
            border: '1px solid #ccc',
            borderRadius: '4px'
          }}
        />
      </div>

      <div style={{ marginBottom: '30px' }}>
        <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
          Password:
        </label>
        <input
          type="password"
          placeholder="Enter password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleConnect()}
          style={{
            width: '100%',
            padding: '10px',
            fontSize: '16px',
            border: '1px solid #ccc',
            borderRadius: '4px'
          }}
        />
      </div>

      <button 
        onClick={handleConnect}
        disabled={isConnecting || !username || !password}
        style={{
          width: '100%',
          padding: '12px',
          fontSize: '16px',
          fontWeight: 'bold',
          backgroundColor: isConnecting || !username || !password ? '#bdc3c7' : '#3498db',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: isConnecting || !username || !password ? 'not-allowed' : 'pointer'
        }}
      >
        {isConnecting ? 'Opening Terminal...' : 'Connect to Server'}
      </button>

      <div style={{ marginTop: '20px', padding: '15px', backgroundColor: '#f8f9fa', borderRadius: '4px' }}>
        <h4>Instructions:</h4>
        <ol>
          <li>Select your assigned server from the dropdown</li>
          <li>Enter your SSH credentials (username and password)</li>
          <li>Click "Connect to Server" to open the terminal in a new window</li>
          <li>Make sure to allow popups for this website</li>
        </ol>
      </div>
    </div>
  );
};

export default StudentDashboard;
