// src/pages/StudentDashboard.js
import React, { useEffect, useRef, useState } from "react";
import { Terminal } from "xterm";
import { FitAddon } from "xterm-addon-fit";
import "xterm/css/xterm.css";
import io from "socket.io-client";

const StudentDashboard = () => {
  const termRef = useRef(null);
  const socketRef = useRef(null);
  const fitAddonRef = useRef(null);
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    // Initialize terminal
    const term = new Terminal({
      cursorBlink: true,
      rows: 20,
      cols: 80,
    });

    const fitAddon = new FitAddon();
    fitAddonRef.current = fitAddon;

    term.loadAddon(fitAddon);

    if (termRef.current) {
      term.open(termRef.current);
      fitAddon.fit();
    }

    // Connect to backend socket
    socketRef.current = io("http://localhost:5000"); // <-- Change if backend runs elsewhere

    socketRef.current.on("connect", () => {
      setConnected(true);
      term.writeln("âœ… Connected to server!");
    });

    socketRef.current.on("output", (data) => {
      term.write(data);
    });

    term.onData((data) => {
      socketRef.current.emit("input", data);
    });

    socketRef.current.on("disconnect", () => {
      setConnected(false);
      term.writeln("\r\nâŒ Disconnected from server");
    });

    // cleanup
    return () => {
      term.dispose();
      socketRef.current.disconnect();
    };
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-2">Student Dashboard</h2>
      <p>Status: {connected ? "ðŸŸ¢ Connected" : "ðŸ”´ Disconnected"}</p>

      <div
        ref={termRef}
        style={{
          width: "100%",
          height: "500px",
          background: "black",
          borderRadius: "8px",
          marginTop: "10px",
        }}
      ></div>
    </div>
  );
};

export default StudentDashboard;
