import React, { useEffect, useRef, useState } from "react";
import { io } from "socket.io-client";
import { Terminal } from "xterm";
import { FitAddon } from "xterm-addon-fit";
import "xterm/css/xterm.css";

export default function StudentDashboard() {
  const termRef = useRef(null);
  const socketRef = useRef(null);
  const terminalInstance = useRef(null);
  const fitAddonRef = useRef(null);

  const servers = [
    { id: 1, ip: "172.184.216.215", description: "Internship Management Project" },
    { id: 2, ip: "20.245.171.127", description: "Internship Management Project" },
  ];

  const [host, setHost] = useState(servers[0].ip);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
    if (!termRef.current) return;

    const term = new Terminal({ cursorBlink: true });
    const fitAddon = new FitAddon();
    terminalInstance.current = term;
    fitAddonRef.current = fitAddon;

    term.loadAddon(fitAddon);
    term.open(termRef.current);
    setTimeout(() => fitAddon.fit(), 50);

    const handleResize = () => fitAddon.fit();
    window.addEventListener("resize", handleResize);

    const socket = io("http://localhost:5000");
    socketRef.current = socket;

    socket.on("ssh-output", (data) => term.write(data));
    term.onData((data) => socket.emit("ssh-input", data));

    return () => {
      socket.disconnect();
      term.dispose();
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const handleConnect = () => {
    if (socketRef.current) {
      socketRef.current.emit("ssh-connect", { host, username, password });
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Student Dashboard - Connect to Your Server</h1>

      {servers.map((server) => (
        <div key={server.id} style={{ marginBottom: "15px", padding: "10px", border: "1px solid #ccc" }}>
          <p><strong>IP:</strong> {server.ip}</p>
          <p><strong>Description:</strong> {server.description}</p>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={{ marginRight: "10px" }}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{ marginRight: "10px" }}
          />
          <button onClick={() => { setHost(server.ip); handleConnect(); }}>Connect</button>
        </div>
      ))}

      <div
        ref={termRef}
        style={{ height: "500px", width: "100%", background: "black", marginTop: "20px" }}
      />
    </div>
  );
}
