import React, { useState, useEffect, useRef, useCallback } from "react";
import { io } from "socket.io-client";
import { Terminal } from "xterm";
import "xterm/css/xterm.css";
import { FitAddon } from "xterm-addon-fit";

const SERVER_URL = "http://localhost:5000";

const TerminalPage = () => {
  const [isConnected, setIsConnected] = useState(false);
  const [connectionData, setConnectionData] = useState(null);
  const [error, setError] = useState("");
  const [connectionStatus, setConnectionStatus] = useState("initializing");
  
  const terminalRef = useRef(null);
  const terminalInstance = useRef(null);
  const fitAddonRef = useRef(null);
  const socketRef = useRef(null);

  const getStoredUserData = useCallback(() => {
    try {
      const userData = localStorage.getItem('user');
      const token = localStorage.getItem('token');
      if (userData) {
        const user = JSON.parse(userData);
        return {
          token: token || '',
          userId: user.id || '',
          userEmail: user.email || '',
          userName: user.name || '',
          registeredId: user.registered_id || '',
          userRole: user.role || 'student'
        };
      }
    } catch (err) {
      console.warn("Could not retrieve stored user data:", err);
    }
    return {};
  }, []);

  const connectToSSHServer = useCallback((socketInstance, data, term) => {
    if (!socketInstance || !data) {
      term?.write(`‚ùå Cannot connect: Missing connection data\r\n`);
      return;
    }
    term?.write(`üîó Connecting to SSH server ${data.host}...\r\n`);
    socketInstance.emit("ssh-connect", {
      host: data.host,
      username: data.username,
      password: data.password,
      user_data: {
        id: data.userId,
        name: data.userName,
        email: data.userEmail,
        role: data.userRole,
        registered_id: data.registeredId
      },
      server_name: data.serverName || data.host
    });
  }, []);

  const cleanupConnection = useCallback(() => {
    socketRef.current?.disconnect();
    terminalInstance.current?.dispose();
    Object.keys(sessionStorage).forEach(key => {
      if (key.startsWith('terminal_')) sessionStorage.removeItem(key);
    });
  }, []);

  const initializeTerminal = useCallback((data) => {
    setConnectionStatus("connecting");

    // Initialize terminal only once
    if (!terminalInstance.current) {
      terminalInstance.current = new Terminal({
        cursorBlink: true,
        fontSize: 14,
        fontFamily: 'Consolas, Monaco, "Courier New", monospace',
        rows: 30,
        cols: 120,
        scrollback: 1000,
        convertEol: true,
        theme: {
          background: '#000000',
          foreground: '#ffffff',
          cursor: '#ffffff'
        }
      });
      fitAddonRef.current = new FitAddon();
      terminalInstance.current.loadAddon(fitAddonRef.current);
      terminalInstance.current.open(terminalRef.current);
      fitAddonRef.current?.fit();
      terminalInstance.current.write("üöÄ Initializing terminal...\r\n");
    }

    // Initialize socket
    const socket = io(SERVER_URL, {
      query: { userId: data.userId || '', userRole: data.userRole || 'student' },
      timeout: 10000,
      forceNew: true
    });
    socketRef.current = socket;

    socket.on("connect", () => {
      setIsConnected(true);
      setConnectionStatus("connected");
      terminalInstance.current?.write(`‚úÖ Socket connected\r\n`);
      connectToSSHServer(socket, data, terminalInstance.current);
    });

    socket.on("ssh-output", (outputData) => {
      terminalInstance.current?.write(outputData);
    });

    socket.on("ssh-connected", (info) => {
      setConnectionStatus("ssh-connected");
      terminalInstance.current?.write(`üéâ SSH Connected: ${info.host}\r\n`);
    });

    socket.on("ssh-error", (err) => {
      const msg = err.message || err;
      terminalInstance.current?.write(`‚ùå SSH Error: ${msg}\r\n`);
      setError(msg);
      setConnectionStatus("error");
    });

    socket.on("disconnect", (reason) => {
      setIsConnected(false);
      setConnectionStatus("disconnected");
      terminalInstance.current?.write(`üîå Disconnected: ${reason}\r\n`);
    });

    // Terminal input
    terminalInstance.current.onData((input) => {
      if (socket.connected) {
        socket.emit("ssh-input", input);
      } else {
        terminalInstance.current?.write(`‚ùå Not connected. Cannot send: ${input}\r\n`);
      }
    });

    // Handle resize safely
    const handleResize = () => fitAddonRef.current?.fit();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);

  }, [connectToSSHServer]);

  useEffect(() => {
    const storedData = getStoredUserData();
    if (storedData.userId) {
      setConnectionData(storedData);
      initializeTerminal(storedData);
    } else {
      setError("No connection data found. Please login first.");
    }

    return () => cleanupConnection();
  }, [getStoredUserData, initializeTerminal, cleanupConnection]);

  const handleDisconnect = () => {
    socketRef.current?.disconnect();
    cleanupConnection();
    window.close();
  };

  if (error) {
    return (
      <div style={{height:'100vh', display:'flex', justifyContent:'center', alignItems:'center', background:'#2c3e50', color:'white'}}>
        <div>
          <h2>‚ö†Ô∏è Terminal Error</h2>
          <p>{error}</p>
          <button onClick={() => window.close()}>Close</button>
        </div>
      </div>
    );
  }

  return (
    <div style={{ height: "100vh", display: "flex", flexDirection: "column", backgroundColor: "#2c3e50" }}>
      <div style={{ padding: '8px', color:'white', background:'#34495e' }}>
        üñ•Ô∏è Terminal | Status: {connectionStatus} | {isConnected ? "Connected" : "Disconnected"}
        <button onClick={handleDisconnect} style={{marginLeft:'10px'}}>‚ùå Disconnect</button>
      </div>
      <div ref={terminalRef} style={{ flex: 1, background: '#000', margin: '5px', border: '1px solid #34495e' }} />
    </div>
  );
};

export default TerminalPage;
