import React, { useState } from "react";
import axios from "axios";

export default function StudentDashboard() {
  const [connected, setConnected] = useState(false);
  const [command, setCommand] = useState("");
  const [output, setOutput] = useState("");
  const [userID] = useState(localStorage.getItem("userId"));

  const connectServer = async () => {
    const res = await axios.post("http://localhost:5000/api/server/connect", {
      userID,
      userName: localStorage.getItem("name"),
      userEmail: localStorage.getItem("email"),
      serverIP: "192.168.1.10", // input field
      username: "ubuntu",
      password: "mypassword"
    });
    if (res.data.message === "Connected") setConnected(true);
  };

  const runCommand = async () => {
    const res = await axios.post("http://localhost:5000/api/server/execute", {
      userID,
      command
    });
    setOutput(res.data.output);
  };

  const disconnectServer = async () => {
    await axios.post("http://localhost:5000/api/server/disconnect", { userID });
    setConnected(false);
    setOutput("");
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold">Student Dashboard</h2>
      {!connected ? (
        <button onClick={connectServer} className="bg-blue-500 text-white px-4 py-2 rounded">
          Connect
        </button>
      ) : (
        <div>
          <input
            type="text"
            placeholder="Enter command"
            value={command}
            onChange={(e) => setCommand(e.target.value)}
            className="border p-2 w-full my-2"
          />
          <button onClick={runCommand} className="bg-green-500 text-white px-4 py-2 rounded mr-2">
            Run
          </button>
          <button onClick={disconnectServer} className="bg-red-500 text-white px-4 py-2 rounded">
            Disconnect
          </button>
          <pre className="bg-black text-green-400 p-4 mt-4">{output}</pre>
        </div>
      )}
    </div>
  );
}
