import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import { Terminal } from "xterm";
import "xterm/css/xterm.css";
import "./StudentDashboard.css";

const SERVER_URL = "http://localhost:5000";

const servers = [
  { name: "Server 1", ip: "172.184.216.215" },
  { name: "Server 2", ip: "20.245.171.127" },
  // Add more servers here
];

const StudentDashboard = () => {
  const [selectedServer, setSelectedServer] = useState(servers[0]);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [socket, setSocket] = useState(null);
  const terminalRef = useRef();

  useEffect(() => {
    const newSocket = io(SERVER_URL);
    setSocket(newSocket);
    const term = new Terminal();
    term.open(terminalRef.current);

    newSocket.on("ssh-output", (data) => {
      term.write(data);
    });

    term.onData((input) => {
      newSocket.emit("ssh-input", input);
    });

    return () => newSocket.disconnect();
  }, []);

  const handleConnect = () => {
    if (!socket) return;
    socket.emit("ssh-connect", {
      host: selectedServer.ip,
      username,
      password,
    });
  };

  return (
    <div className="dashboard-container">
      <h2>Student Dashboard</h2>

      <div className="server-list">
        {servers.map((server) => (
          <div
            key={server.ip}
            className={`server-item ${selectedServer.ip === server.ip ? "selected" : ""}`}
            onClick={() => setSelectedServer(server)}
          >
            {server.name} - {server.ip}
          </div>
        ))}
      </div>

      <input
        type="text"
        className="input-field"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />

      <input
        type="password"
        className="input-field"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      <button className="connect-btn" onClick={handleConnect}>
        Connect
      </button>

      <div
        ref={terminalRef}
        style={{ width: "100%", height: "500px", marginTop: "20px", background: "black" }}
      />
    </div>
  );
};

export default StudentDashboard;
