import React, { useState } from 'react';
import { Monitor, Server, Terminal, User, Lock, Wifi } from 'lucide-react';

const StudentDashboard = () => {
  const [connections, setConnections] = useState({});
  const [loading, setLoading] = useState({});

  // Your 5 server IPs
  const servers = [
    { id: 1, ip: '172.184.216.215', name: 'Server 1' },
    { id: 2, ip: '20.245.171.127', name: 'Server 2' },
    { id: 3, ip: '192.168.1.100', name: 'Server 3' },
    { id: 4, ip: '10.0.0.50', name: 'Server 4' },
    { id: 5, ip: '203.112.45.89', name: 'Server 5' }
  ];

  const handleInputChange = (serverId, field, value) => {
    setConnections(prev => ({
      ...prev,
      [serverId]: {
        ...prev[serverId],
        [field]: value
      }
    }));
  };

  const handleConnect = async (server) => {
    const { ip, id } = server;
    const credentials = connections[id];
    
    if (!credentials?.username || !credentials?.password) {
      alert('Please enter both username and password');
      return;
    }

    setLoading(prev => ({ ...prev, [id]: true }));

    try {
      // Create terminal page URL with connection details
      const terminalUrl = `/terminal?host=${ip}&username=${encodeURIComponent(credentials.username)}&password=${encodeURIComponent(credentials.password)}&server=${encodeURIComponent(server.name)}`;
      
      // Open terminal in new tab
      const newWindow = window.open(terminalUrl, '_blank');
      
      if (!newWindow) {
        alert('Please allow popups for this site to open terminal windows');
      } else {
        // Clear password for security
        setConnections(prev => ({
          ...prev,
          [id]: {
            ...prev[id],
            password: ''
          }
        }));
      }
    } catch (error) {
      console.error('Connection error:', error);
      alert('Failed to open terminal connection');
    } finally {
      setLoading(prev => ({ ...prev, [id]: false }));
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900">
      {/* Header */}
      <div className="bg-slate-800/50 backdrop-blur-sm border-b border-slate-700">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex items-center space-x-3">
            <Terminal className="w-8 h-8 text-blue-400" />
            <h1 className="text-2xl font-bold text-white">Student Dashboard</h1>
            <div className="flex items-center space-x-2 ml-auto">
              <Wifi className="w-5 h-5 text-green-400" />
              <span className="text-green-400 text-sm">Online</span>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-6 py-8">
        <div className="mb-8">
          <h2 className="text-xl font-semibold text-white mb-2">Available Servers</h2>
          <p className="text-slate-400">Connect to any of the hosting servers below using your credentials</p>
        </div>

        {/* Server Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-6">
          {servers.map((server) => (
            <div key={server.id} className="bg-slate-800/50 backdrop-blur-sm rounded-xl border border-slate-700 p-6 hover:border-blue-500/50 transition-all duration-300">
              {/* Server Header */}
              <div className="flex items-center space-x-3 mb-4">
                <div className="p-2 bg-blue-500/20 rounded-lg">
                  <Server className="w-6 h-6 text-blue-400" />
                </div>
                <div>
                  <h3 className="text-lg font-semibold text-white">{server.name}</h3>
                  <p className="text-blue-400 font-mono text-sm">{server.ip}</p>
                </div>
              </div>

              {/* Connection Form */}
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-slate-300 mb-2">
                    <User className="w-4 h-4 inline mr-1" />
                    Username
                  </label>
                  <input
                    type="text"
                    placeholder="Enter username"
                    value={connections[server.id]?.username || ''}
                    onChange={(e) => handleInputChange(server.id, 'username', e.target.value)}
                    className="w-full px-3 py-2 bg-slate-700/50 border border-slate-600 rounded-lg text-white placeholder-slate-400 focus:outline-none focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-slate-300 mb-2">
                    <Lock className="w-4 h-4 inline mr-1" />
                    Password
                  </label>
                  <input
                    type="password"
                    placeholder="Enter password"
                    value={connections[server.id]?.password || ''}
                    onChange={(e) => handleInputChange(server.id, 'password', e.target.value)}
                    className="w-full px-3 py-2 bg-slate-700/50 border border-slate-600 rounded-lg text-white placeholder-slate-400 focus:outline-none focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                    onKeyPress={(e) => e.key === 'Enter' && handleConnect(server)}
                  />
                </div>

                <button
                  onClick={() => handleConnect(server)}
                  disabled={loading[server.id]}
                  className="w-full bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 disabled:from-slate-600 disabled:to-slate-700 text-white font-medium py-2.5 px-4 rounded-lg transition-all duration-200 flex items-center justify-center space-x-2"
                >
                  {loading[server.id] ? (
                    <>
                      <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                      <span>Connecting...</span>
                    </>
                  ) : (
                    <>
                      <Terminal className="w-4 h-4" />
                      <span>Connect</span>
                    </>
                  )}
                </button>
              </div>

              {/* Connection Status */}
              <div className="mt-4 pt-4 border-t border-slate-700">
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-slate-500 rounded-full"></div>
                  <span className="text-slate-400 text-xs">Ready to connect</span>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Instructions */}
        <div className="mt-8 bg-slate-800/30 rounded-xl border border-slate-700 p-6">
          <h3 className="text-lg font-semibold text-white mb-3 flex items-center">
            <Monitor className="w-5 h-5 mr-2 text-blue-400" />
            How to Connect
          </h3>
          <div className="text-slate-300 space-y-2">
            <p>1. Select a server from the list above</p>
            <p>2. Enter your username and password for that server</p>
            <p>3. Click "Connect" to open a terminal session in a new tab</p>
            <p>4. You can connect to multiple servers simultaneously</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
