// src/pages/StudentDashboard.js
import React, { useEffect, useRef, useState } from "react";
import { io } from "socket.io-client";
import { Terminal } from "xterm";
import { FitAddon } from "xterm-addon-fit";
import "xterm/css/xterm.css";

export default function StudentDashboard() {
  const termRef = useRef(null); // terminal container
  const socketRef = useRef(null); // socket connection
  const terminalInstance = useRef(null); // Terminal instance
  const fitAddonRef = useRef(null); // FitAddon instance

  // Optional: Inputs for server credentials
  const [host, setHost] = useState("192.168.1.100");
  const [username, setUsername] = useState("test");
  const [password, setPassword] = useState("1234");

  useEffect(() => {
    if (!termRef.current) return;

    // 1️⃣ Create Terminal
    const term = new Terminal({
      cursorBlink: true,
      rows: 20,
      cols: 80,
    });
    terminalInstance.current = term;

    // 2️⃣ Create FitAddon
    const fitAddon = new FitAddon();
    fitAddonRef.current = fitAddon;
    term.loadAddon(fitAddon);

    // 3️⃣ Open terminal in div
    term.open(termRef.current);

    // 4️⃣ Fit terminal to container
    fitAddon.fit();

    // 5️⃣ Re-fit on window resize
    const handleResize = () => fitAddon.fit();
    window.addEventListener("resize", handleResize);

    // 6️⃣ Connect socket
    const socket = io("http://localhost:5000");
    socketRef.current = socket;

    socket.on("ssh-output", (data) => term.write(data));
    term.onData((data) => socket.emit("ssh-input", data));

    // 7️⃣ Clean up on unmount
    return () => {
      socket.disconnect();
      term.dispose();
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  // Connect button
  const handleConnect = () => {
    if (socketRef.current) {
      socketRef.current.emit("ssh-connect", { host, username, password });
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Student Dashboard - Connect to Your Server</h1>

      {/* Server credentials form */}
      <div style={{ marginBottom: "10px" }}>
        <input
          type="text"
          placeholder="Server IP"
          value={host}
          onChange={(e) => setHost(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <button onClick={handleConnect}>Connect</button>
      </div>

      {/* Terminal */}
      <div
        ref={termRef}
        style={{ height: "500px", width: "100%", background: "black" }}
      />
    </div>
  );
}
